/********************************************************************************
* 기      능   : 조회(COUNT)
********************************************************************************/
function fn_query() {
	//필수체크	
	if(gfn_isNull(ed_pjt, true, st_pjt.value))
		return;

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps04:PS0408001A_S1");
	tit_addSearchActionInfo("ps07:PS0709001A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery2"   //"fn_afterQuery"
        , true
        , true);


   ufn_enable_chk('init');     

}



/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
