/********************************************************************************
* 기      능   : print 40
********************************************************************************/
function fn_print()
{  

	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}


//trace(ds_list_p.SaveXML());
//alert("1");
	ds_list_p.ClearData();
	ds_list.Filter("CHECK==1");	
	ds_list_p.copyF(ds_list);
	ds_list.unFilter();


	ds_list_p.SetColumn(0, "chk_1_1", chk_1_1.Value);
	ds_list_p.SetColumn(0, "chk_1_2", chk_1_2.Value);
	ds_list_p.SetColumn(0, "chk_1_3", chk_1_3.Value);
	ds_list_p.SetColumn(0, "chk_2_1", chk_2_1.Value);
	ds_list_p.SetColumn(0, "chk_2_2", chk_2_2.Value);
	ds_list_p.SetColumn(0, "chk_3_1", chk_3_1.Value);
	ds_list_p.SetColumn(0, "chk_3_2", chk_3_2.Value);
	ds_list_p.SetColumn(0, "chk_4_1", chk_4_1.Value);
	ds_list_p.SetColumn(0, "chk_4_2", chk_4_2.Value);
	ds_list_p.SetColumn(0, "chk_5_1", chk_5_1.Value);
	ds_list_p.SetColumn(0, "chk_5_2", chk_5_2.Value);
	ds_list_p.SetColumn(0, "chk_6_1", chk_6_1.Value);
	ds_list_p.SetColumn(0, "chk_6_2", chk_6_2.Value);


trace(ds_list_p.SaveXML());
//alert("2");

	Dialog("PS07::PS0709001P.xml", "gbn=a", -1, -1, "Resize=true");


}

/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectGridCol(obj);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}

}
/********************************************************************************
* 기      능   : 원프로젝트 팝업
********************************************************************************/
function btn_pjt_OnClick(obj)
{
	//var arg = "pjt="+quote(ed_pjt.Text);
	//gfn_openPopup("PS0408001C",true);
	var arg = "pjt="+quote(ed_pjt.Text);
	gfn_openPopup("CS9601002A", true, arg);

	fn_query();
}





/********************************************************************************
* 기      능   : 체크박스 이벤트
********************************************************************************/
function chk_1_OnClick(obj,strValue)
{   
	switch(obj.id)
	{
		case "chk_1_1" :
			if(strValue == '1')
			{	chk_1_2.value = "";  chk_1_3.value = "";	}
			break;			
		case "chk_1_2" :
			if(strValue == '1')
			{	chk_1_1.value = "";  chk_1_3.value = "";	}
			break;						
		case "chk_1_3" :
			if(strValue == '1')
			{	chk_1_1.value = "";  chk_1_2.value = "";	}
			break;								
	}
}

