/********************************************************************************
* 기      능   : 데이타셋  변경 이벤트
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{	
	if(strColumnID=="CHECK")
	{
		//if(varNewValue==0)
		//{ 	return;	 }

		if(obj.Sum("CHECK") > 1)
		{
			gfn_showAlert("CW00116","한 호기만 ");
			ds_list.SetColumn(nRow, strColumnID, 0);
			return;
		}
		else if(obj.Sum("CHECK") < 1)
		{
			ufn_enable_chk('init');
			return;
		}

		if( obj.getColumn(nRow, "TYPE") == "MR" && obj.getColumn(nRow, "CHECK") == "1" )   ufn_enable_chk("MR");						
		if(obj.getColumn(nRow, "USES") == "비상용" && obj.getColumn(nRow, "CHECK") == "1" )   ufn_enable_chk("비상용");								


		/*=======영업사양에 따라 자동 체크박스구성================*/	
        ufn_enable_chk2( 
                         ds_list.GetColumn(nRow, "EL_EHP"), 
                         ds_list.GetColumn(nRow, "EL_EHM"),
                         ds_list.GetColumn(nRow, "EL_EHTT"),
                         ds_list.GetColumn(nRow, "EL_EHPB")
                        );						

	}

}


/********************************************************************************
* 기      능   : 사용자 함수(체크박스 헨들링)
********************************************************************************/
function ufn_enable_chk(gbn)
{
	if(gbn == "init")
	{
		chk_1_1.Value = 0;	chk_1_2.Value = 0;	chk_1_3.Value = 0;
		chk_2_1.Value = 0;	chk_2_2.Value = 0;
		chk_3_1.Value = 0;	chk_3_2.Value = 0;
		chk_4_1.Value = 0;	chk_4_2.Value = 0;
		chk_5_1.Value = 0;	chk_5_2.Value = 0;
		chk_6_1.Value = 0;	chk_6_2.Value = 0;

		chk_5_1.Enable = false;	chk_5_2.Enable = false;
		chk_6_1.Enable = false;	chk_6_2.Enable = false;	
	}

	if(gbn == "MR")
	{
		chk_5_1.Enable = true;	chk_5_2.Enable = true;
	}

	if(gbn == "비상용")
	{
		chk_6_1.Enable = true;	chk_6_2.Enable = true;
	}	

}

/********************************************************************************
* 기      능   : 사용자 함수(체크박스 헨들링)
********************************************************************************/
function ufn_enable_chk2(EL_EHP, EL_EHM, EL_EHTT, EL_EHPB)
{

    //====================승강로 재질======================//
	if( EL_EHM == "CEMEN" || EL_EHM == "BRICK")  //콘크리드
	{
		chk_1_1.Value = 1;
		if( chk_5_1.Enable == true )  chk_5_1.Value = 1;		
		if( chk_6_1.Enable == true )  chk_6_1.Value = 1;	
	}
	else if( EL_EHM == "STGL" || EL_EHM == "STSP") //유리
	{
		chk_1_3.Value = 1;
		if( chk_5_3.Enable == true )  chk_5_3.Value = 1;		
		if( chk_6_3.Enable == true )  chk_6_3.Value = 1;
	}
	else if( EL_EHM == "STWL"  )  //내화구조
	{
		chk_1_2.Value = 1;
		if( chk_5_2.Enable == true )  chk_5_2.Value = 1;		
		if( chk_6_2.Enable == true )  chk_6_2.Value = 1;
	}


    //====================승강로 PIT ======================//
    if( EL_EHP >= 2500 )  
    { chk_4_1.Value = 1;  }
    else
    { chk_4_2.Value = 1;  }

    //================승강장 문턱간 거리(11m)===================//
    if( EL_EHTT >= "Y" )  
    { chk_2_1.Value = 1;  }
    else
    { chk_2_2.Value = 1;  }

    //================피트하부 공간==================//
    if( EL_EHPB >= "Y" )  
    { chk_3_1.Value = 1;  }
    else
    { chk_3_2.Value = 1;  }

}
