function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);
	gfn_initDs(ds_cond, true);

	btn_copy.Enable = false;
	btn_sms.Enable = false;
	ed_pjt.SetFocus();

	userGroup = gfn_getUserGroup();	

	if(userGroup == '10') ds_cond.SetColumn(0,"GUBUN",'10');
	else if(userGroup == '30') ds_cond.SetColumn(0,"GUBUN","30");
	else ds_cond.SetColumn(0,"GUBUN","");

	ds_cond.SetColumn(0, "USER_NAME", G_USER_NAME);

    tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710001A_S2");
	tit_addSearchActionInfo("ps07:PS0710001A_S4");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_temno=ds_temno ds_combo2=ds_combo2"
		, ""
		, "fn_afterTemno"
		, true,true);
}
