/********************************************************************************
* 기      능   : 신청 55
********************************************************************************/
function fn_request() {
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
			// 필수 입력 항목	
			if(length(ds_list.GetColumn(i, "CHK_KIND"))<=0 ){
				var arr = [parseInt(i) + 1, "검사종류"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
			if(ds_list.GetColumn(i, "REQ_DATE")=='00000000'|| ds_list.GetColumn(i, "REQ_DATE")==''){
				var arr = [parseInt(i) + 1, "검사희망일"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
			if(length(ds_list.GetColumn(i, "PERNO")) <=0){
				var arr = [parseInt(i) + 1, "설치팀장"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
			if(length(ds_list.GetColumn(i, "REQ_CELL")) <=0){
				var arr = [parseInt(i) + 1, "팀장 HP"];
				gfn_showMsg("CW00224", arr);
				return;
			}
			if(length(ds_list.GetColumn(i, "CODE2"))<=0 ){
				var arr = [parseInt(i) + 1, "검사기관"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
		}
	}

	var nFindRow = ds_list.SearchRow("CHECK=='1' && AUSE!='PS' && WORMO > 0 && (CHK_KIND = 'A' || CHK_KIND = 'B' || CHK_KIND = 'F' || CHK_KIND = 'G' || CHK_KIND = 'D')");
	if(nFindRow >= 0)
	{
		/*
		gfn_confirm("수주 사양에 공사용사용 "+ds_list.GetColumn(nFindRow, "WORMO")+"개월이 존재합니다.\n일반적으로 공사용사용이 있는 승강기는"+
					"\n첫 완성검사를 '인승'으로 용도지정합니다.\n'인승'으로 변경할까요?","용도지정 체크")*/

		if(gfn_confirm("수주 사양에 공사용사용 "+ds_list.GetColumn(nFindRow, "WORMO")+"개월이 존재합니다.\n일반적으로 공사용사용이 있는 승강기는"+
					"\n첫 완성검사를 '인승'으로 용도지정합니다.\n'인승'으로 변경할까요?","용도지정 체크"))
		{
			ds_list.fireevent = false;
			for(var i=0; i<ds_list.GetRowCount();i++)
			{
				if(ds_list.GetColumn(i, "CHECK") == 1)
				{
					ds_list.SetColumn(i, "AUSE", "PS");
				}
			}
			ds_list.fireevent = true;
		}
	}

	var arg = "";	
	// 설치 재검(건축,업체)
	var nSearchRow = ds_list.SearchRow("CHECK == 1 && (CHK_KIND == 'F' || CHK_KIND == 'G')");
	if(nSearchRow >= 0)
	{
		arg = " chk_kind="+quote(ds_list.GetColumn(nSearchRow,"CHK_KIND"));
		arg += " memo="+quote(ds_list.GetColumn(nSearchRow,"BIGO"));
		gfn_openPopup("PS0710001B",true,arg); // 안내사항 팝업 호출
	}
	else
	{
		var nSearchRow2 = ds_list.SearchRow("CHECK == 1 && CHK_KIND == 'B'");	// 설치-재검
		if(nSearchRow2 >=0)
		{
			arg = " chk_kind="+quote(ds_list.GetColumn(nSearchRow2,"CHK_KIND"));
			arg += " memo="+quote(ds_list.GetColumn(nSearchRow2,"BIGO"));
			gfn_openPopup("PS0710001B",true,arg); // 안내사항 팝업 호출
		}
		else
		{
			fn_save();
		}

	}


}
