********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( errCode != 0 ) 
	{
		gfn_showMsg("CE00001");
	} 
	else 
	{
		gfn_showMsg("CI00003");
		gfn_openMsgWin("신청 되었습니다.");
	}
	fv_gbn = "";

	fn_query();
}
