/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_list.fireevent = false;
	if(strColumnID!="CHECK"){
		ds_list.SetColumn(nRow, "CHECK", 1);
	}

	switch( strColumnID ) {
		case "CHK_KIND":	// CHK_KIND = 'A' : 완성, 'B' : 완성(재검), 'C' : 수시, 'D' : 수시(교체)
			if(ds_list.GetColumn(nRow,"CHK_KIND") == "A" && ds_list.GetColumn(nRow, "CHK_REQ_INDC") == "Y")	// 완성검사 신청 : 완성,완성(재검),수시,수시(교체) 신청내역 있으면 ERROR
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("설치검사 신청이 이미 완료되었습니다. 설치검사 신청을 할 수 없습니다.");
				break;
			}
			else if(ds_list.GetColumn(nRow,"CHK_KIND") == "D" && ds_list.GetColumn(nRow, "CHK_REQ_INDC") == "Y")	// 교체검사 신청 : 완성,완성(재검),수시,수시(교체) 신청내역 있으면 ERROR
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("설치(교체)를 신청한 이력이 있어 재검으로 신청하여야 합니다.");
				break;
			}
			/*
			else if(ds_list.GetColumn(nRow,"CHK_KIND") == "D" && ds_list.GetColumn(nRow,"ISREMD") != "Y")	// 수시교체 신청, 교체여부에 Y가 없으면 신청불가 - 2020.07.17 고범석 책임 요청으로 주석처리.
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				//var arr = ["신청","신청"];
				gfn_showMsg("교체공사만 신청가능합니다.");
				break;
			}*/
			else if((ds_list.GetColumn(nRow,"CHK_KIND") == "C" || ds_list.GetColumn(nRow,"CHK_KIND") == "E" || ds_list.GetColumn(nRow,"CHK_KIND") == "H" || ds_list.GetColumn(nRow,"CHK_KIND") == "I") && ds_list.GetColumn(nRow,"SEOLCHI_INDC") == 'N') 
			// 수시: C, E, H, I 를 신청하려는 경우, A, B, F, G, D  신청 이력이 없으면 에러
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("설치검사(또는 설치재검) 접수가 안되었습니다. 설치검사 접수 후 수시검사 신청 가능합니다.");
				break;
			}
			else if(ds_list.GetColumn(nRow,"CHK_KIND") == "C" && ds_list.GetColumn(nRow,"SUSI_YN") == 'N') // 수시: 수시검사 접수 이력이 있으면 에러
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("수시검사를 신청한 이력이 있어 재검으로 신청하여야 합니다.");
				break;
			}
			/* 2019-04-08 고범석 GJ 요청 = 수시재검 시 완성검사 체크로직 제거 
			else if(ds_list.GetColumn(nRow,"CHK_KIND") == "E" && ds_list.GetColumn(nRow,"KINDA_YN") == 'Y') // 수시재검: 수시검사 접수가 되어있으면 에러
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("완성검사(또는 완성재검) 접수가 안되었습니다. 완성검사 접수 후 수시재검 신청 가능합니다.");
				break;
			}*/
			else if((ds_list.GetColumn(nRow,"CHK_KIND") == "E" || ds_list.GetColumn(nRow,"CHK_KIND") == "H" || ds_list.GetColumn(nRow,"CHK_KIND") == "I") && ds_list.GetColumn(nRow,"SUSI_REQ_INDC") == 'N') 
			// 수시재검: E, H, I 를 신청하려는 경우 C 신청 이력이 없으면 에러
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("수시검사 접수가 안되었습니다. 수시검사 접수 후 수시재검 신청 가능합니다.");
				break;
			}

			else if((ds_list.GetColumn(nRow,"CHK_KIND") == "B" || ds_list.GetColumn(nRow,"CHK_KIND") == "F" || ds_list.GetColumn(nRow,"CHK_KIND") == "G") && ds_list.GetColumn(nRow,"SEOLCHI_REQ_INDC") == 'N') 
			// B, F, G 를 신청하려는 경우 A 신청 이력이 없으면 에러
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("설치검사를 신청한 이력이 없습니다. 설치검사를 먼저 진행해 주시기 바랍니다.");
				break;
			}
			else if((ds_list.GetColumn(nRow,"CHK_KIND") == "C") && ds_list.GetColumn(nRow,"CHK_COMPL_INDC") == "N")
			{
				ds_list.SetColumn(nRow,"CHECK", 0);
				ds_list.SetColumn(nRow,"CHASU","");
				ds_list.SetColumn(nRow,"CHK_KIND","");
				gfn_showMsg("수시검사 신청은 설치/수시검사 수검 화면에서 1차 검사일 또는 검사완료일이 있는 경우에만 가능 합니다.");
				break;
			}
		case "PERNO":
			ds_list.SetColumn(nRow,"REQ_CELL",ds_temno.GetColumn(ds_temno.FindRow("CODE",ds_list.GetColumn(nRow,"PERNO")),"CELLP"));
			break;
	}	
	ds_list.fireevent = true;
}
// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	var widths = this.Width - 160;
	var heights = this.Height - 160;

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}	

	cal_temp.Visible = true;
	cal_temp.value = ds_list.getColumn(nRow, "REQ_DATE");	
	cal_temp.SetFocus();
}
