/********************************************************************************
* 기      능   : 그리드의 특정로우 데이타를 해당로우에 복사
********************************************************************************/
function btn_copy_OnClick(obj)
{
	var nRow = 0;

	ds_range.DeleteAll();
	ds_range.LoadXML(gfn_openPopup("PS9601001A", true,""));
//trace(ds_range.SaveXML());
	ds_list.fireevent = false;
	grd_list.Redraw = false;

	ds_range.rowpos = ds_range.firstrow;
	while(ds_range.currow <= ds_range.lastrow) {
		nRow = ds_range.GetColumn(ds_range.currow,"ROW");
		if(nRow <> -1) {

			if(ds_list.GetColumn(nRow,"STATE") == 'Y')
			{
				//ds_list.SetColumn(nRow,"CHECK", 1);													//Check Box
				ds_list.SetColumn(nRow,"CHK_KIND",	ds_list.GetColumn(ds_list.currow,"CHK_KIND"));	//검사종류
				ds_list.SetColumn(nRow,"AUSE",		ds_list.GetColumn(ds_list.currow,"AUSE"));		//용도지정
				ds_list.SetColumn(nRow,"REQ_DATE",	ds_list.GetColumn(ds_list.currow,"REQ_DATE"));	//검사요청일
				ds_list.SetColumn(nRow,"CODE2",		ds_list.GetColumn(ds_list.currow,"CODE2"));		//검사기관
				ds_list.SetColumn(nRow,"PERNO",		ds_list.GetColumn(ds_list.currow,"PERNO"));		//신청인
				ds_list.SetColumn(nRow,"REQ_CELL",	ds_list.GetColumn(ds_list.currow,"REQ_CELL"));	//신청인HP
			}

		}

		if(ds_range.currow == ds_range.lastrow) break;
		ds_range.rowpos++;
	}

	grd_list.Redraw = true;
	ds_list.fireevent = true;

}


/********************************************************************************
* 기      능   : SMS 보내기
********************************************************************************/
function btn_sms_OnClick(obj)
{
	/*==============영업준공미승인분에대한 SMS 전송==============*/
		var lv_msg = "";
		var nAddRow = 0;
		ds_list2.Clear();
		//ds_list.Filter("CHK_KIND=='A' && STATE!='Y'");
		ds_list.Filter("STATE!='Y'");
		ds_list2.copyF(ds_list);
		ds_list.unFilter();

		ds_list2.Sort("PSPID,POSID",True);


		ds_log.clearData();

		for(var i=0; i<ds_list2.GetRowCount(); i++)
		{
			if(i == 0)
			{
				tit_clearActionInfo();
				tit_addSearchActionInfo("ps07:PS0710001A_S3");
				tit_callService(
					""
					, ""
					, "ds_cond=ds_cond"
					, "ds_sms=ds_sms"
					, ""
					, ""
					, ""
					, ""
					, true);


				lv_msg = ds_list2.GetColumn(i,"PSPID") + '프로젝트의(' ;
			}	

			if(i+1 < ds_list2.GetRowCount())
			{
				lv_msg = lv_msg + substr(ds_list2.GetColumn(i,"POSID"),6,3) + ',' ;
			}
			else
			{
				lv_msg = lv_msg + substr(ds_list2.GetColumn(i,"POSID"),6,3) + '호기)준공승인요청드립니다.'  ;
			}

		}

		for(var i=0; i<ds_list2.GetRowCount(); i++)
		{
			// 로그 테이블에 남기도록 처리
			nAddRow = ds_log.AddRow();
			ds_log.SetColumn(nAddRow, "GBN", "S");
			ds_log.SetColumn(nAddRow, "CLSS", "PS0710001A");
			ds_log.SetColumn(nAddRow, "GBN_DESC", "문자");
			ds_log.SetColumn(nAddRow, "CLSS_DESC", "완성/수시검사 신청 SMS발송");
			ds_log.SetColumn(nAddRow, "SEND_USER", G_USER_ID);
			ds_log.SetColumn(nAddRow, "RECV_USER", ds_sms.GetColumn(0, "TRAN_PHONE"));
			ds_log.SetColumn(nAddRow, "PHONE", ds_sms.GetColumn(0, "TRAN_PHONE"));
			ds_log.SetColumn(nAddRow, "EMAIL", "");
			ds_log.SetColumn(nAddRow, "MESSAGE", lv_msg);
			ds_log.SetColumn(nAddRow, "PSPID", ds_list2.GetColumn(i,"PSPID"));
			ds_log.SetColumn(nAddRow, "POSID", ds_list2.GetColumn(i,"POSID"));
		}
//lv_msg = '117471프로젝트의(L01,L02,S01,S02,W01,W02,W03,W04호기)준공승인요청드립니다.';

//ALERT(lv_msg);


		if(ds_list2.GetRowCount() > 0)
		{

			ds_sms.SetColumn(0,"TRAN_ETC1", "PS0710001A");
			//ds_sms.SetColumn(0,"TRAN_TYPE", "1");
			//ds_sms.SetColumn(0,"TRAN_STATUS", "1");
			ds_sms.SetColumn(0,"TRAN_TYPE", "0");
			ds_sms.SetColumn(0,"TRAN_STATUS", "0");
			ds_sms.SetColumn(0,"TRAN_CALLBACK", "설치");
			ds_sms.SetColumn(0,"TRAN_MSG", lv_msg);

//alert(ds_sms.getColumn(0,"TRAN_ETC1"));
//alert(ds_sms.getColumn(0,"TRAN_CALLBACK"));
//alert(ds_sms.getColumn(0,"TRAN_MSG"));
//trace(ds_sms.SaveXML())	;

			tit_clearActionInfo();
			tit_addMultiActionInfo("ps07:PS0710001A_I2");
			tit_addMultiActionInfo("ps07:PS0710001A_I3");
			tit_callService(
			  "PDA_ACT"
			, ""
			, "ds_sms=ds_sms ds_log=ds_log"
			, ""
			, ""
			, ""
			, ""
			, ""
			, true);

		}		
		/*===========================================================*/
}

function fn_sendMail(nRow)
{
	// 메일전송 미리보기 열기
	// 보내는 사람 - 로그인 유저
	var send_user = 'helco' + G_USER_ID +  '@hdel.co.kr';

	// 받는사람 - 영업사원
	var recv_user    = ds_list.GetColumn(nRow, "ZMAN_MAIL");
	//'helco' + Substr(ds_list.GetColumn(nRow, "ZMAN"),1,7) + '@hdel.co.kr';
	//Trace(recv_user);
	var recv_user_nm = ds_list.GetColumn(nRow, "ZMAN_NM");

	// 참조
	var cc_user = send_user;
	var cc_user_nm   = G_USER_NAME;

	// 제목
	var mesgtitl = '완성검사를 위한 준공 승인 요청 ('+ds_list.GetColumn(nRow, "PSPID")+'-'+Substr(ds_list.GetColumn(nRow, "ZSITE_NM"),0,7)+')' ;
	//Trace(Substr(ds_list.GetColumn(nRow, "ZSITE_NM"),0,7));

	// 내용
	var mesgbody = "";

    mesgbody = ds_list.GetColumn(nRow, "DEPT_N") +' 지사 ' + G_USER_NAME +' 님이 SRM을 통해 발송된 메일입니다. ' + '\n\n\n'; 

	mesgbody = mesgbody + '프로젝트번호: ' + ds_list.GetColumn(nRow, "PSPID") + '\n';
	//mesgbody = mesgbody +'호기번호: ' + ds_head.GetColumn(0, "POSID") + '\n';
    mesgbody = mesgbody + '프로젝트명: ' + ds_list.GetColumn(nRow, "ZSITE_NM") + '\n\n';

    mesgbody = mesgbody + '준공승인이 나지 않아 완성검사를 신청하지 못하고 있습니다.' + '\n';
    mesgbody = mesgbody + '당 현장 수금 확인 바랍니다.' + '\n\n';
	mesgbody = mesgbody + '감사합니다.' + '\n' ;


	// 메일 형태 만들기
	ds_mail.ClearData();
	ds_mail.addRow();

	ds_mail.SetColumn(0, "SEND_USER_ID", G_USER_ID);
	ds_mail.SetColumn(0, "RECV_USER_ID", Substr(ds_list.GetColumn(nRow, "ZMAN"),1,7));
	ds_mail.SetColumn(0, "SEND_USER", send_user);
	ds_mail.SetColumn(0, "SEND_USER_NM", G_USER_NAME);


	ds_mail.SetColumn(0, "RECV_USER", recv_user);
	ds_mail.SetColumn(0, "RECV_USER_NM", recv_user_nm);


	ds_mail.SetColumn(0, "CC_USER", cc_user);
	ds_mail.SetColumn(0, "CC_USER_NM", cc_user_nm);

	ds_mail.SetColumn(0, "MESGTITL", mesgtitl);
	ds_mail.SetColumn(0, "MESGBODY", mesgbody);
	ds_mail.SetColumn(0, "PSPID", ds_list.GetColumn(nRow, "PSPID"));
	ds_mail.SetColumn(0, "POSID", ds_list.GetColumn(nRow, "POSID"));


	gfn_openPopup("PS0710001M",true);
}


function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 12)
	{
		fn_sendMail(nRow);
	}

}
