function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	gfn_initDs(ds_cond, true);

	//copy tool 비활성화
	btn_copy.Enable = false;
	userGroup = gfn_getUserGroup();	

	var codeArr = ["PS003"];
    var dsObjArr = [ds_combo];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    tit_clearActionInfo();
    tit_addSearchActionInfo("ps07:PS0710001A_S2");
	tit_addSearchActionInfo("ps07:PS0701001A_S4");
	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_temno=ds_temno ds_combo2=ds_combo2"
		, ""
		, "fn_afterLifnr"
		, true,true);


	chk_so3.Value = '1';
	chk_so4.Value = '1';

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) 
{

	cb_actss.Index = 0;

	// 일자 setting 
	var currYear = left(gfn_getCurrDate(), 6);
	redat_from.Value = currYear+"01";
	redat_to.Value = addDate((parseInt(currYear)+1)+"0101", -1);

	cb_gbn1.Index = 0;
	cb_gbn2.Index = 0;
	cb_gbn3.Index = 0;

	fn_btn();

}
/********************************************************************************
* 기      능   : 조회 Callback  - 리스트의 검사기관 조회
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );

	cb_zzpmnum.Index = 0;

	// 통합관리 화면에서 넘어왔을 경우
	fv_gbn = s_gbn;

	if(fv_gbn == 'SELECT')
	{
		ds_cond.SetColumn(0, "PSPID", s_pspid);
		ds_cond.SetColumn(0, "GBN2", s_chkKind);
		ds_cond.SetColumn(0, "SELECT", 'Y');
		fn_query();
	}
}
/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}
