/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	ds_list.fireevent = false;
	if(strColumnID == "AUSE" || strColumnID == "REQ_DATE"  || strColumnID == "CHK_LIFNR" || strColumnID == "BIGO")
	{
		ds_list.SetColumn(nRow, "CHECK", 1);
	}
	ds_list.fireevent = true;
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++){
		if(ds_list.GetColumn(i, "CHECK")==1){
			// 필수 입력 항목	
			if(ds_list.GetColumn(i, "REQ_DATE")=='00000000'|| ds_list.GetColumn(i, "REQ_DATE")==''){
				var arr = [parseInt(i) + 1, "검사희망일"];
				gfn_showMsg("CW00224", arr);
				return;
			} 
		}
	}

	ds_list_tmp.ClearData();
	ds_list.Filter("CHECK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();


	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0710003A_U1");
	tit_callService(
		""
		, ""
		, "ds_list_tmp=ds_list_tmp"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""
		, true);
}

/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() 
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1)
	{
		gfn_showMsg('CW00046');
		return;
	}



	ds_list_tmp.ClearData();
	ds_list.Filter("CHECK==1");
	ds_list_tmp.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps07:PS0710003A_S3", "", "ps07:PS0710003A_D1");	// 완성검사 접수/수검 데이터 삭제 ZPST0201
	tit_addSelectExActionInfo("ps07:PS0710003A_S4", "", "ps07:PS0710003A_D2");  // 수시검사 접수/수검 데이터 삭제 ZPST0202
	tit_addSelectExActionInfo("ps07:PS0710003A_S5", "", "ps07:PS0710003A_D3");  // KETF 접수현황 데이터 삭제 ZPST0203
	tit_addMultiActionInfo("ps07:PS0710003A_U2"); // ZPST0200
	tit_callService(
		""
		, ""
		, "ds_list_tmp=ds_list_tmp"
		, ""
		, ""
		, "fn_afterDel"
		, ""
		, ""
		, true);
}

