/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	colum = ds_cond.GetColumn(ds_cond.Row, "TEXT");
	row   = ds_text.SearchRow("CODE=='"+colum+"'");


	if ( !gfn_checkFromToObj(cal_from, cal_to, true, ds_text.GetColumn(row, "CODE_NAME"), 0, true) ) {
			return;
	}

	ds_cond.SetColumn(0,"CO_GBN","");

	var qry = "";
	var args = "";

	// 조회 기간(일자)  1년 이내 범위로 제한. 
	gfn_getDayCnt(ds_dayCnt, cal_from.Value, cal_to.Value);
	nDays = ds_dayCnt.GetColumn(0, "DAYS");

	//프로젝트 번호가 없으면 6개월 제한
	if(Length(Trim(ed_pjt.Text)) < 6)
	{
		if(nDays > 180)
		{
			gfn_openMsgWin("조회 범위는 6개월(180일)이내로 제한됩니다.");
			cal_from.Value = gfn_getAfterDate(gfn_getCurrDate(),-180);
			cal_to.Value = gfn_getCurrDate();
			return;
		}
	}
	else	//프로젝트 번호가 있으면 1년 제한
	{
		if(nDays > 365)
		{
			gfn_openMsgWin("조회 범위는 1년이내로 제한됩니다.");
			cal_from.Value = gfn_getAfterDate(gfn_getCurrDate(),-365);
			cal_to.Value = gfn_getCurrDate();
			return;
		}
	}


	/*
	if(chk_co1.Value == '1' || chk_co2.Value == '1' || chk_co3.Value == '1') 
	{
		qry += " AND (B.ZZGUBUN IN (";
		if(chk_co1.Value == '1') qry+= "'10','13'";
		if(chk_co2.Value == '1') qry+= "'11','12','14'";
		if(chk_co3.Value == '1') qry+= "'20',";
		qry+= "'')) ";
		ds_cond.SetColumn(0,"CO_GBN","C");
	}	


	args = " SQRY="+quote(qry);	
	*/

	tit_clearActionInfo();	
	tit_addSearchActionInfo("ps07:PS0710004A_S1");
	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , ""
        , true
        , true);     

}
