/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		if(ds_list.GetColumn(i, "CHECK")==1)
		{
			if(( (ds_list.GetColumn(i, "KETF1_DATE") <= '00000000' || length(ds_list.GetColumn(i, "KETF1_DATE")) <> 8) && (ds_list.GetColumn(i, "KETF2_DATE") <= '00000000' || length(ds_list.GetColumn(i, "KETF2_DATE")) <> 8) ))
			{
				var arr = [parseInt(i) + 1, "1차접수일 or 2차접수일 "];
				gfn_showMsg("CW00055", arr);
				return;
			} 

			if(ds_list.GetColumn(i, "KETF1_DATE") > '00000000' && ds_list.GetColumn(i, "KETF1_DATE") < '20000101')
			{
				gfn_openMsgWin("["+parseInt(i) + 1 +"번째 행의] 1차접수일을\n2000년도 이후로 선택해주세요.");
				return;
			}

			if(ds_list.GetColumn(i, "KETF2_DATE") > '00000000' && ds_list.GetColumn(i, "KETF2_DATE") < '20000101')
			{
				gfn_openMsgWin("["+parseInt(i) + 1 +"번째 행의] 2차접수일을\n2000년도 이후로 선택해주세요.");
				return;
			}

		}
	}

	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();


	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps07:PS0710004A_S2", "ps07:PS0710004A_I1", "ps07:PS0710004A_U1");
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""		
		, true);				

}	
