/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) 
	{
		gfn_selectGridCol(obj); 
	} 
	else if ( nCell == 1 ) {
		return;
	}
	else if ( nCell == 2) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}

}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

  if(strColumnID <> "FLAG")
  {
	ds_list.fireevent = false;
	if(strColumnID!="CHECK"){
		ds_list.SetColumn(nRow, "CHECK", 1);
	}
	else
	{
		var sChkIndc = ds_cond.GetColumn(0, "CHK_INDC");
		var sCheck   = ds_list.GetColumn(nRow, "CHECK");
		var sPspid   = ds_list.GetColumn(nRow, "PSPID");
		var sChkKind = ds_list.GetColumn(nRow, "CHK_KIND");
		var sChasu   = ds_list.GetColumn(nRow, "CHASU");
		var sReqDate2 =  ds_list.GetColumn(nRow, "REQ_DATE2");

		if(sCheck == 1)
		{
			if(sChkIndc == 1)	// 일괄체크 && 체크버튼 클릭 시 프로젝트,검사종류,차수,검사요청일이 같은 데이터 일괄 select
			{
				for(var i=0 ; i<ds_list.GetRowCount(); i++)
				{
					if(sPspid == ds_list.GetColumn(i, "PSPID") && sChkKind == ds_list.GetColumn(i, "CHK_KIND") 
						&& sChasu == ds_list.GetColumn(i, "CHASU") && sReqDate2 == ds_list.GetColumn(i, "REQ_DATE2") && ds_list.GetColumn(i, "CHECK") != 1)
					{
						ds_list.SetColumn(i, "CHECK", 1);

					}
				}
			}
		}

	}
	ds_list.fireevent = true;
   }
}
//달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	var widths = this.Width - 160;
	var heights = this.Height - 160;

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}	

	cal_temp.Visible = true;


	if(nCell == 22)	{	// 1차 접수(a)
		cal_temp.value = ds_list.getColumn(nRow, "KETF1_DATE");	
	} else if(nCell == 24)	{	// 2차 접수
		cal_temp.value = ds_list.getColumn(nRow, "KETF2_DATE");	
	} 


	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();


	if(grd_list.GetClickedCell() == 22)	{	// 1차 접수(a)
		ds_list.setColumn(ds_list.row, "KETF1_DATE", strText);
	} else if(grd_list.GetClickedCell() == 24)	{	// 2차 접수
		ds_list.setColumn(ds_list.row, "KETF2_DATE", strText);
	} 


	obj.Visible = false;
}
