/********************************************************************************
* 기      능   : 사업부 선택시 ds_lifnr 데이터셋 값 filtering
********************************************************************************/
function chk_so_OnClick(obj,strValue)
{
	var so = ' ';
	var so2 = ' ';
	var so3 = ' ';
	var so4 = ' ';
	var so5 = ' ';
	var so7 = ' ';

	if(chk_so.Value == '1')  so =  'X';
	if(chk_so2.Value == '1') so2 = 'X';
	if(chk_so3.Value == '1') so3 = 'X';
	if(chk_so4.Value == '1') so4 = 'X';
	if(chk_so5.Value == '1') so5 = 'X';
	if(chk_so7.Value == '1') so7 = 'X';


	if(chk_so.Value+chk_so2.Value+chk_so3.Value+chk_so4.Value+chk_so5.Value+chk_so7.Value == '000000')
	{
		ds_lifnr.Filter("SO5=='' || CODE==''");
	}	
	else
	{
	  ds_lifnr.Filter("CODE=='' || SO=="+quote(so) +" || SO2=="+quote(so2) +" || SO3=="+quote(so3) +" || SO4=="+quote(so4) +" || SO5=="+quote(so5) +" || SO7=="+quote(so7));
	}


	ds_lifnr.Sort("CODE_NAME");

	cb_zzlifnr.Index = 0;	
}

/********************************************************************************
* 기      능   : 관리코드 생성 버튼 클릭 시
********************************************************************************/
function btn_keyGener_OnClick(obj)
{
	var sPspid   = "";
	var sChkKind = "";
	var sChasu   = "";
	var sReqDate = "";

	if(ds_list.SearchRow("CHECK == '1'") < 0)
	{
		// 관리코드를 생성할 데이터가 선택되지 않았습니다.
		gfn_showMsg("관리코드를 생성할 데이터가 선택되지 않았습니다.");
		return;
	}

	for(var i=0; i<ds_list.GetRowCount(); i++)
	{
		sChk     = ds_list.GetColumn(i, "CHECK");

		if(sChk == '1')	// 선택된 데이터에 한해
		{
			sPspid   = ds_list.GetColumn(i, "PSPID");
			sChkKind = ds_list.GetColumn(i, "CHK_KIND");
			sChasu   = ds_list.GetColumn(i, "CHASU");
			sReqDate2 = ds_list.GetColumn(i, "REQ_DATE2");

			// 완성/수시구분, 차수, 프로젝트번호가 다른 데이터는 하나의 관리코드로 생성 불가
			var nFindRow = ds_list.SearchRow("CHECK == '1' && (PSPID != '"+sPspid+"' || CHK_KIND != '"+sChkKind+"' || CHASU != '"+sChasu+"' || REQ_DATE2 != '"+sReqDate2+"')");
			if(nFindRow >= 0)
			{
				gfn_showMsg("검사종류, 차수, 프로젝트번호, 확정검사요청일이 다른 데이터는 동일한 관리코드로 생성이 불가합니다.");
				return;
			}
		}
	}

	//생성 시 관리코드가 있는 데이터는 생성 불가.
	var nFindRow = ds_list.SearchRow("CHECK == '1' && LENGTH(KEYCODE) != 0");

	if(nFindRow >= 0)
	{
		// 관리코드가 존재하는 데이터는 삭제 불가합니다.
		gfn_showMsg("관리코드가 존재하는 데이터는 생성 불가합니다.");
		return;
	}

	var nRow2 = ds_list.SearchRow("CHECK == '1'");

	ds_keyCond.ClearData();
	ds_keyCond.addRow();
	ds_keyCond.SetColumn(0, "PSPID", ds_list.GetColumn(nRow2, "PSPID"));
	ds_keyCond.SetColumn(0, "CHK_KIND", ds_list.GetColumn(nRow2, "CHK_KIND"));
	ds_keyCond.SetColumn(0, "CHASU", ds_list.GetColumn(nRow2, "CHASU"));
	ds_keyCond.SetColumn(0, "ZZACTSS", ds_list.GetColumn(nRow2, "ZZACTSS"));

	// 관리코드 생성 (동기)
	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710004A_S3");
	tit_callService(
		""
		, ""
		, "ds_keyCond=ds_keyCond"
		, "ds_keyGen=ds_keyGen"
		, ""		
		, ""
		, ""
		, ""
		, true);

	for(var i=0;i<ds_list.GetRowCount();i++)
	{
		sChk = ds_list.GetColumn(i,"CHECK");

		if(sChk == 1)
		{
			ds_list.SetColumn(i, "KEYCODE", ds_keyGen.getColumn(0, "KEYCODE"));
		}
	}


	//생성된 관리코드 반영하여 데이터 생성
	ds_list.Filter("CHECK==1");
	ds_keyList.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0710004A_I2");
	tit_callService(
		""
		, ""
		, "ds_keyList=ds_keyList"
		, ""
		, ""
		, "fn_afterkeyGen"
		, ""
		, ""
		, true);

}

