/********************************************************************************
* 기      능   : 관리코드 삭제 버튼 클릭 시
********************************************************************************/
function btn_keyDel_OnClick(obj)
{
	var sPspid   = "";
	var sChkKind = "";
	var sChasu   = "";
	var sReqDate = "";

	if(ds_list.SearchRow("CHECK == '1'") < 0)
	{
		// 관리코드를 삭제할 데이터가 선택되지 않았습니다.
		gfn_showMsg("관리코드를 삭제할 데이터가 선택되지 않았습니다.");
		return;
	}

	// 관리코드 삭제
	ds_list.Filter("CHECK==1");
	ds_keyList.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	tit_addMultiActionInfo("ps07:PS0710004A_D1");
	tit_callService(
		""
		, ""
		, "ds_keyList=ds_keyList"
		, ""
		, ""
		, "fn_afterkeyDel"
		, ""
		, ""
		, true);
}

/********************************************************************************
* 기      능   : 관리코드 삭제 후 CALLBACK
********************************************************************************/
function fn_afterkeyDel(errCode, errMsg) {
	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		for(var i=0 ; i <ds_list.GetRowCount() ; i++)
		{
			var sChk = ds_list.GetColumn(i, "CHECK");

			if(sChk == '1')
			{
				ds_list.SetColumn(i, "KEYCODE", "");
			}
		}
	}

}



/********************************************************************************
* 기      능   : 관리코드 관리
********************************************************************************/
function btn_keyManage_OnClick(obj)
{
	var arg = "s_pspid="+quote(ds_cond.GetColumn(0, "PSPID"));
	Trace(ds_cond.GetColumn(0, "PSPID"));
	gfn_openWin("PS0710004B",arg);

}

/********************************************************************************
* 기      능   : 화물용 저장
********************************************************************************/
function btn_saveHwamul_OnClick(obj)
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();


	tit_clearActionInfo();
	tit_addSelectExActionInfo("ps07:PS0710004A_S2", "ps07:PS0710004A_I3", "ps07:PS0710004A_U2");
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2"
		, ""
		, ""
		, "fn_afterSave"
		, ""
		, ""		
		, true);	
}
