function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	gfn_initDs(ds_cond, true);

	//기술서류 상태
	ds_cond.SetColumn(0, "STATUS", "D");
	ds_cond.SetColumn(0, "TEXT", "B");
	ds_cond.SetColumn(0, "EXCPT_INDC", "0");

	//분동수수료 default 체크
	chk_boon.Value = '1';

	//copy tool 비활성화
	btn_copy.Enable = false;
	userGroup = gfn_getUserGroup();	

	var codeArr = ["PS003"];
    var dsObjArr = [ds_combo];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710001A_S2");
	tit_addSearchActionInfo("ps07:PS0710001A_S4");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_temno=ds_temno ds_combo2=ds_combo2"
		, ""
		, "fn_afterTemno"
		, true,true);

}
/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	if(gfn_getUserGroup() == "30")
	{
		ds_combo.filter("CODE == " + quote( G_AREA_CODE_E) );
	}
	cb_actss.Index = 0;
	cb_gbn.Index = 0;
	cb_docStus.Index = 0;

	// 일자 setting 
	var currYear = left(gfn_getCurrDate(), 6);
	cal_from.Value = currYear+"01";
	cal_to.Value = addDate((parseInt(currYear)+1)+"0101", -1);

	ed_pjt.Enable = true;
	btn_popProj.Enable = true;

}
/********************************************************************************
* 기      능   : 조회 Callback  - 리스트의 검사기관 조회
********************************************************************************/
function fn_afterTemno(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );

	cb_zzpmnum.Index = 0;

	// 통합관리 화면에서 넘어왔을 경우
	fv_gbn = s_gbn;

	if(fv_gbn == 'SELECT')
	{

		ds_cond.SetColumn(0, "PSPID", s_pspid);
		ds_cond.SetColumn(0, "GBN", s_chkKind);
		ds_cond.SetColumn(0, "SELECT", 'Y');
		fn_query();
	}
}
