/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	// 신청일, 승인일, 기술서류접수일 별 미입력 조회조건 검사

	var sCode = ds_cond.GetColumn(0, "TEXT");
	var nRow = ds_text.SearchRow("CODE=='"+sCode+"'");
	var sValue = "";
	if(nRow >= 0)
	{
		sValue = ds_text.GetColumn(nRow, "CODE_NAME");
	}

	if ( !gfn_checkFromToObj(cal_from, cal_to, true, sValue, 0, false) ) {
			return;
	}

	// 조회 기간(일자)  1년 이내 범위로 제한. 
	gfn_getDayCnt(ds_dayCnt, cal_from.Value, cal_to.Value);
	nDays = ds_dayCnt.GetColumn(0, "DAYS");

	if(nDays > 365)
	{
		gfn_openMsgWin("조회 범위는 1년이내로 제한됩니다.");
		cal_from.Value = gfn_getAfterDate(gfn_getCurrDate(),-365);
		cal_to.Value = gfn_getCurrDate();
		return;
	}


	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710005A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"  
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);

}
