/********************************************************************************
* 기      능   : Delete 
********************************************************************************/
function fn_delete() {
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	//#### 민원24 접수 전 데이터 삭제처리

	// #1. 접수 데이터 삭제. ZPST0201 OR ZPST0202 에서 데이터 삭제

	// #2. KETF접수현황 ZPST0203 데이터 삭제

	// #3. 관리코드 매핑 테이블에서 데이터 삭제 ZPST0046

	// #4. 완성/수시검사 최초 승인일 이력 삭제 - ZPST0200F 에서 데이터 삭제

	// #5. 완성/수시검사 신청 데이터 ZPST0200 REQ_GBN = '1'(신청), CFM_DATE = '00000000' 돌리기

	ds_list2.clearData();
	ds_list3.clearData();

	ds_list.Filter("CHECK==1 && (CHK_KIND=='A' || CHK_KIND=='B' || CHK_KIND=='F' || CHK_KIND=='G' || CHK_KIND=='D')");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	ds_list.Filter("CHECK==1 && (CHK_KIND=='C' || CHK_KIND=='E' || CHK_KIND=='H' || CHK_KIND=='I')");
	ds_list3.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	if(ds_list2.RowCount() > 0)
	{
		tit_addMultiActionInfo("ps07:PS0710005A_D1");	// 접수(ZPST0201 테이블 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_D3");	//  KETF접수현황(ZPST0203 테이블 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_D5");	//  관리코드 매핑(ZPST0046 테이블 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_D7");	// 최초 승인일 이력 삭제(ZPST0200F 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_U4");	// 완성/수시검사 신청 데이터 ZPST0200 REQ_GBN = '1'(신청), CFM_DATE = '00000000'

	}	
	if(ds_list3.RowCount() > 0) 
	{
		tit_addMultiActionInfo("ps07:PS0710005A_D2");	// 접수(ZPST0202 테이블 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_D4");	// KETF접수현황(ZPST0203 테이블 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_D6");	// 관리코드 매핑(ZPST0046 테이블 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_D8");	// 최초 승인일 이력 삭제(ZPST0200F 삭제)
		tit_addMultiActionInfo("ps07:PS0710005A_U5");	// 완성/수시검사 신청 데이터 ZPST0200 REQ_GBN = '1'(신청), CFM_DATE = '00000000'

	}	
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterDel"
		, true);

}
