/********************************************************************************
* 기      능   : 그리드 헤더 클릭시 정렬 처리 
********************************************************************************/
function grd_list_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	if ( nCell == 0 ) {
		return;
	}
	else if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 데이터셋 값 변경 시 - Flag 처리 
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{

	ds_list.fireevent = false;
	if(strColumnID=="CHECK" && varNewValue == "1"){
		ds_list.SetColumn(nRow, "CHECK", 1);
		ds_list.SetColumn(nRow, "FLAG", "U");
	}else if(strColumnID=="CHECK" && varNewValue == "0"){
		ds_list.SetColumn(nRow, "CHECK", 0);
		ds_list.SetColumn(nRow, "FLAG", "");

		// 체크를 없앨 경우, 값도 원래대로 처리
		var nWrbtr = ds_list.getOrgColumn(nRow, "WRBTR");
		ds_list.SetColumn(nRow, "WRBTR", nWrbtr);

	}else{
		ds_list.SetColumn(nRow, "CHECK", 1);
		ds_list.SetColumn(nRow, "FLAG", "U");
	}
	ds_list.fireevent = true;
}
// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 
********************************************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	var rect = obj.GetCellRect(nRow, ncell);
	var widths = this.Width - 160;
	var heights = this.Height - 160;

	cal_temp.top = rect[1] + 60;
	if (cal_temp.top > heights)
	{
		cal_temp.top = heights;
	}
	cal_temp.Left = rect[0] + 10;
	if (cal_temp.Left > widths)
	{
		cal_temp.Left = widths;
	}	

	cal_temp.Visible = true;

	if(nCell == 17)	{	// 확정검사요청일
		cal_temp.value = ds_list.getColumn(nRow, "CNFM_REQ_DATE");	
	} else if(nCell == 21)	{	// 민원24 접수일
		cal_temp.value = ds_list.getColumn(nRow, "RCPT_DATE");	
	}	
	cal_temp.SetFocus();
}
/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	gfn_clearMsgBar();

	if(grd_list.GetClickedCell() == 17)	{	// 확정검사요청일
		ds_list.setColumn(ds_list.row, "CNFM_REQ_DATE", strText);
	} else if(grd_list.GetClickedCell() == 21)	{	// 검사접수일
		ds_list.setColumn(ds_list.row, "RCPT_DATE", strText);
	}	

	obj.Visible = false;
}
/********************************************************************************
