/********************************************************************************
* 기      능   :  수수료 계산버튼 클릭시 선급금 금액계산
********************************************************************************/
function btn_boon_OnClick(obj)
{
	var amt;

	// 재검사를 기준으로 하여 계산
	if(chk_boon.Value == '0')	// 분동수수료 미체크 시
	 {
		  for(var i = 0; i < ds_list.RowCount(); i++) {

			   if(ds_list.GetColumn(i, "CHECK") == true) {

				   if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC4_GBN") == 'A1') {
						amt = 92900;                                                                                                   
					} else if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC4_GBN") == 'A2') {
						amt = 116500 ;                                                                                                
					} else if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC4_GBN") == 'A3') {
						amt = 94700 ;                                                                                                 
					} else if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'B' && ds_list.GetColumn(i,"SPEC4_GBN") == 'A1') {
						amt = 92800 ;                                                                                                 
					} else if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'B' && ds_list.GetColumn(i,"SPEC4_GBN") == 'A3') {
						amt =  94300;                                                     
					} else if(ds_list.GetColumn(i,"HOGI1_GBN") == 'B' ) {                  
						amt = 83200 ;                                                      
					} else if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'C') {
						amt = 35700 ;
					} else{
						amt = 0;
					} 
				  ds_list.Setcolumn(i, "WRBTR", amt);
				 }
			   }
	 }
	else if(chk_boon.Value == '1')	// 분동수수료 체크시
	  {
		for(var i = 0; i < ds_list.RowCount(); i++) {

			 if(ds_list.GetColumn(i, "CHECK") == true) {

				 if(ds_list.GetColumn(i,"HOGI1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC1_GBN") == 'A' && ds_list.GetColumn(i,"SPEC4_GBN") == 'A1') {
					//92900 + 73000
