		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 승강기 자기적합확인서 출력
********************************************************************************/
function fn_print1() 
{
	ds_list_p3.ClearData();

	for(var i=0; i < ds_list.GetRowCount() ; i++)
	{
		var nChk = ds_list.GetColumn(i, "CHECK");

		if(nChk == 1)
		{
			var nRow = ds_list_p3.addRow();
			ds_list_p3.SetColumn(nRow, "POST1", ds_list.GetColumn(i, "POST1"));
			ds_list_p3.SetColumn(nRow, "ADDR", ds_list.GetColumn(i, "ADDR"));
			ds_list_p3.SetColumn(nRow, "MODEL", ds_list.GetColumn(i, "MODEL"));
			ds_list_p3.SetColumn(nRow, "NAMET", G_USER_NAME);
		}

	}
    // 승강기 자기적합 확인서 출력
    Dialog("PS07::PS0701003P.xml", "", -1, -1, "Resize=true");
}

/********************************************************************************
* 기      능   : 신고서 출력
********************************************************************************/
function fn_print2()
{
	if(ds_list.RowCount() == 0)
	{
		gfn_showMsg("CW00010");
		return;
	}
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	ds_list2.ClearData();
	ds_list.Filter("CHECK==1");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	var pspid = "";
	var arg = "";
	var param = "";
	var param2 = "";
	//var kind = "";

	for(var i=0; i<ds_list2.GetRowCount(); i++)
	{		
		if(pspid != "" && pspid != ds_list2.GetColumn(i,"PSPID"))
		{
			gfn_showMsg("CW00204","프로젝트");
			return;
		}
		else
		{
			pspid = ds_list2.GetColumn(i,"PSPID");
		}

		arg += "'"+ds_list2.GetColumn(i,"POSID")+"'"+",";		
	}

	if(substr(arg,length(arg)-1) == ",") arg = substr(arg,0,length(arg)-1);

	param = "  AND A.POSID IN ("+arg+")";
	param2 = "  AND X.POSID_1 IN ("+arg+")";

	ds_cond_p.ClearData();
	ds_cond_p.AddRow();
	ds_cond_p.SetColumn(0,"PSPID",pspid);
	ds_cond_p.SetColumn(0,"GUBUN",param);
	ds_cond_p.SetColumn(0,"GUBUN2",param2);


	ds_list_p.ClearData();
	ds_list_p2.ClearData();
	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps07:PS0705002A_S7");
	tit_addSearchActionInfo("ps07:PS0707001A_S11");

	tit_callService(
        ""
        , ""
        , "ds_cond_p=ds_cond_p"
        , "ds_list_p=ds_list_p"
        , ""
        , "fn_afterQuery2"
        , true);
}

/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery2(errCode, errMsg) 
{
	var cnt1 = 0;
	var cnt2 = 0;

	ds_list_p1.ClearData();
	ds_list_p2.ClearData();

	for(var i=0; i<ds_list_p.GetRowCount(); i++)
	{
		if( i <= 6 )
		{	
			cnt1 = ds_list_p1.AddRow();
			ds_list_p1.CopyRow(cnt1, "ds_list_p", i);					
		}
		else
		{		
			cnt2 = ds_list_p2.AddRow();		
			ds_list_p2.CopyRow(cnt2, "ds_list_p", i);			
		}
	}

	Dialog("PS07::PS0707001P.xml", -1, -1, "Resize=true");

}

//# 저장버튼 클릭 장시 - 확정검사요청일, 메모 저장
function Button1_OnClick(obj)
{
	// 그리드 저장 대상 로우
	if(ds_list.FindRow("CHECK", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	ds_list2.clearData();
	ds_list3.clearData();

	ds_list.Filter("CHECK==1 && (CHK_KIND=='A' || CHK_KIND=='B' || CHK_KIND=='F' || CHK_KIND=='G' || CHK_KIND=='D')");
	ds_list2.copyF(ds_list);
	ds_list.unFilter();

	ds_list.Filter("CHECK==1 && (CHK_KIND=='C' || CHK_KIND=='E' || CHK_KIND=='H' || CHK_KIND=='I')");
	ds_list3.copyF(ds_list);
	ds_list.unFilter();

	tit_clearActionInfo();
	if(ds_list2.RowCount() > 0)
	{
		tit_addMultiActionInfo("ps07:PS0710005A_U1");
	}	
	if(ds_list3.RowCount() > 0) 
	{
		tit_addMultiActionInfo("ps07:PS0710005A_U3");
	}	
	tit_callService(
		""
		, ""
		, "ds_list2=ds_list2 ds_list3=ds_list3"
		, ""
		, ""
		, "fn_afterSave"
		, true);
}

/********************************************************************************
* 기      능   : 사무소 변경 시 -> PM조회
********************************************************************************/
function cb_actss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_cond.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S42");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, true,true);
}

/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZzactssChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
}
