/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	if ( !gfn_checkFromToObj(cal_from, cal_to, true, st_date.value, 0, false) ) {
			return;
	}


	if(cb_gbn.Value == 'A' || cb_gbn.Value == 'B' || cb_gbn.Value == 'F' || cb_gbn.Value == 'G' || cb_gbn.Value == 'D')
	{
		ds_cond.SetColumn(0,"GBNA","A");
		ds_cond.SetColumn(0, "GBNALL","N");
	}
	else if(cb_gbn.Value == 'C' || cb_gbn.Value == 'E' || cb_gbn.Value == 'H' || cb_gbn.Value == 'I')
	{
		ds_cond.SetColumn(0,"GBNC","C");
		ds_cond.SetColumn(0, "GBNALL","N");
	}
	else {
		ds_cond.SetColumn(0,"GBNA","A");
		ds_cond.SetColumn(0,"GBNC","C");
		ds_cond.SetColumn(0, "GBNALL","Y");
	}

	// 조회 기간(일자)  1년 이내 범위로 제한. 
	gfn_getDayCnt(ds_dayCnt, cal_from.Value, cal_to.Value);
	nDays = ds_dayCnt.GetColumn(0, "DAYS");

	if(nDays > 365)
	{
		gfn_openMsgWin("조회 범위는 1년이내로 제한됩니다.");
		cal_from.Value = gfn_getAfterDate(gfn_getCurrDate(),-365);
		cal_to.Value = gfn_getCurrDate();
		return;
	}

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710006A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"  
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);

     //   trace(ds_list.SaveXML());
}
