function fn_OnLoadCompleted(obj) {
	gfn_initForm(true);

	gfn_initDs(ds_cond, true);

	userGroup = gfn_getUserGroup();	

	// 진행현장만 보기 세팅
	rd_chk.Value = "A";

	// 사무소 가져오기
	var codeArr = ["PS003"];
    var dsObjArr = [ds_combo];
    var addTypeArr = [G_CODE_ALL];
    gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

    tit_clearActionInfo();
    tit_addSearchActionInfo("ps07:PS0710001A_S2");
	tit_addSearchActionInfo("ps02:PS0201001A_S34");		// 협력업체
	tit_addSearchActionInfo("ps07:PS0701001A_S4");		// 
	tit_addSearchActionInfo("ps07:PS0710009A_S2");		// 영업지사
	//tit_addSearchActionInfo("ps07:PS0710009A_S3");	// 영업담당자

	tit_callService(
		""
		, ""
		, "ds_cond_cd=ds_cond"
		, "ds_temno=ds_temno ds_lifnr=ds_zzlifnr_cd ds_combo2=ds_combo2 ds_vkgrp=ds_vkgrp"
		, ""
		, "fn_afterLifnr"
		, true,true);




}
/********************************************************************************
