function fn_completeInit(sCode, sDsObj, sCodeType) 
{
	cb_actss.Index = 0;
	cb_gbn2.Index = 0;
	rd_gbn.Index = 0;
	// 일자 setting gfn_getAfterMonth(gfn_getCurrMonth(),3)

	redat_from.Value = gfn_getAfterDate(gfn_getCurrDate(),-90);
	redat_to.Value   = gfn_getAfterDate(gfn_getCurrDate(),30);





}
/********************************************************************************
* 기      능   : 조회 Callback  - 리스트의 검사기관 조회
********************************************************************************/
function fn_afterLifnr(errCode, errMsg) {
	if( userGroup == '10' )
	{
		ds_lifnr.Filter("CODE=="+quote(G_VEND_CODE_E));
		cb_zzlifnr.Index  = 0;
		cb_zzlifnr.Enable = false;
	}
	else
	{	
		ds_lifnr.Filter("SO5==''");
		gfn_insertEmptyRow( ds_lifnr, G_CODE_ALL );
		cb_zzlifnr.value = "";	
	}	

	gfn_insertEmptyRow( ds_vkgrp, G_CODE_ALL );
	//gfn_insertEmptyRow( ds_sman, G_CODE_ALL );
	cb_vkgrp.value = "";	
	//cb_sman.value = "";	
	cb_vkgrp.Index = 0;
	//cb_sman.Index = 0;

	//Trace(SubStr(G_USER_ID,0,1));
	if(SubStr(G_USER_ID,0,1) == '9')
	{

	}
	else
	{
		div_sman.ed_sman_cd.Value = "";
		div_sman.ed_sman_nm.Value = "";
	}


}
