********************************************************************************/
function fn_query() 
{
	if(G_USER_ID == '2029847')
	{
			 File0.FileName = "C:\\Users\\User\\Documents\\test.ini";          // C:\Users\User\Documents 폴더에 test.ini 생성해 둠
			 if( File0.open("a+") )                  {                               
						var len = File0.write("START :  "+getDate() + (Timem() - toFloat(Time()+"000"))+"\n",-1,"utf-8");                            
						File0.close();
			 }

	}

	// 영업담당자 세팅
	ds_cond.SetColumn(0, "SMAN",   div_sman.ed_sman_cd.Value);

	//사무소 또는 프로젝트 필수 입력
	var sActss = ds_cond.GetColumn(0, "ZZACTSS");
	var sPjtNo = ds_cond.GetColumn(0, "PSPID");
	var sRdChk = ds_cond.GetColumn(0, "RD_CHK");


	if(sRdChk == 'B')	// 착공예정일
	{
		if(gfn_isNull(cb_actss, false) && gfn_isNull(ed_pjt, false) && (div_sman.ed_sman_cd.Value == '' || length(div_sman.ed_sman_cd.Value) == 0))
		{
			gfn_showMsg("CW00207","프로젝트 또는 설치지사/영업담당자");
			return;
		}
	}
	else	// 진행현장만 보기
	{
		if(gfn_isNull(ed_pjt, false) && gfn_isNull(cb_actss, false))
		{
			gfn_showMsg("CW00207","프로젝트 또는 설치지사");
			return;
		}
	}


	//var curTime = getDate() + (Timem() - toFloat(Time()+"000"));
	//Trace("시작 : "+ToString(curTime,"@@@@년 @@월 @@일 @@시 @@분 @@초 @@@"));



    tit_clearActionInfo();
	tit_addSearchActionInfo("ps07:PS0710009A_S1");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_list=ds_list"  
		, ""
		, "fn_afterQuery"
		, ""
		, ""
		, true);


}



