/********************************************************************************
* 기      능   : 사무소 변경 시 -> PM조회
********************************************************************************/
function cb_actss_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_cond.SetColumn(0,"ZZACTSS",strCode);
	cb_zzpmnum.value = "";

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps02:PS0201001A_S42");
	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, "ds_zzpmnum_cd=ds_zzpmnum_cd"
		, ""
		, "fn_afterZzactssChange"
		, true,true);
}

/********************************************************************************
* 기      능   : 조회 Callback  - 사무소 변경시
********************************************************************************/
function fn_afterZzactssChange(errCode, errMsg) {
	gfn_insertEmptyRow( ds_zzpmnum_cd, G_CODE_ALL );
	cb_zzpmnum.value = "";
}




/********************************************************************************
* 기      능   : 그리드 더블클릭 이벤트 - 신청/승인/접수/수검일 클릭시 해당화면으로 이동
********************************************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var sChk = "";
	var sChkKind = ds_list.GetColumn(nRow, "CHK_KIND");
	var sStus    = ds_list.GetColumn(nRow, "STATUS");
	var sCk = "";


	if(sChkKind == 'A' || sChkKind == 'B' || sChkKind == 'F' || sChkKind == 'G' || sChkKind == 'D')
	{
		sCk = 'A';
	}
	else	// C,E,H,I
	{
		sCk = 'C';
	}


	if(nCell == 4)	// 신청이력
	{
		if(ds_list.GetColumn(nRow, "APPLY_HIS") <> "보기")
		{
			return;
		}

		arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
			  " s_posid="+quote(ds_list.GetColumn(nRow, "POSID")) +
			  " s_chasu="+quote(ds_list.GetColumn(nRow, "CHASU")) +
			  " s_chkKind="+sCk;

		gfn_openPopup("PS0710009B",false,arg);
	}
	else if(nCell == 35)	// 메일발송
	{
		fn_sendMail(nRow);
	}
	else if(nCell == 20)	// 신청일
	{
		sChk = ds_list.GetColumn(nRow, "CDATE");
		if(sChk != '00000000')
		{
			return;
		}

		if(sStus != 'A')
		{
			gfn_openMsgWin("신청 단계가 아닙니다.");
			return;
		}

		// 신청화면으로 연결
		arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
			  " s_gbn='SELECT'";

		gfn_openWin("PS0710001A",arg);  
	}
	else if(nCell == 21)	// 승인일
	{
		sChk = ds_list.GetColumn(nRow, "CFM_DATE");

		if(sChk != '00000000')
		{
			return;
		}

		if(sStus != 'B')
		{
			gfn_openMsgWin("승인 단계가 아닙니다.");
			return;
		}


		// 신청/승인 화면으로 연결
		arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
			  " s_chkKind="+sCk +
			  " s_gbn='SELECT'";

		gfn_openWin("PS0710003A",arg);  
	}
	else if(nCell == 25)	// 접수일
	{
		sChk = ds_list.GetColumn(nRow, "RCPT_DATE");

		if(sChk != '00000000')
		{
			return;
		}

		if(sStus != 'C' && sStus != 'D')
		{
			gfn_openMsgWin("접수 단계가 아닙니다.");
			return;
		}

		// 접수화면으로 연결
		arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
			  " s_chkKind="+sCk +
			  " s_gbn='SELECT'";

		gfn_openWin("PS0710005A",arg);  
	}	
	else if(nCell == 32)	// 수검일
	{
		sChk = ds_list.GetColumn(nRow, "FIN_DATE");

		if(sChk != '00000000')
		{
			return;
		}

		if(sStus != 'E')
		{
			gfn_openMsgWin("수검 단계가 아닙니다.");
			return;
		}

		// 수검화면으로 연결
		arg = "s_pspid="+quote(ds_list.GetColumn(nRow, "PSPID")) +
			  " s_chkKind="+sCk +
			  " s_gbn='SELECT'";

		gfn_openWin("PS0710007A",arg);  
	}
}

function fn_sendMail(nRow)
{
	// 메일전송 미리보기 열기
	// 보내는 사람 - 로그인 유저
	var send_user = 'helco' + G_USER_ID +  '@hdel.co.kr';

	// 받는사람 - 영업사원
	var recv_user    = 'helco' + Substr(ds_list.GetColumn(nRow, "ZMAN"),1,7) + '@hdel.co.kr';
	var recv_user_nm = ds_list.GetColumn(nRow, "ZMAN_NM");

	// 참조
	var cc_user = send_user;
	var cc_user_nm   = G_USER_NAME;

	// 제목
	var mesgtitl = '완성검사를 위한 준공 승인 요청 ('+ds_list.GetColumn(nRow, "PSPID")+'-'+Substr(ds_list.GetColumn(nRow, "ZSITE_NM"),0,7)+')' ;

	// 내용
	var mesgbody = "";

    mesgbody = ds_list.GetColumn(nRow, "DEPT_N") +' 지사 ' + G_USER_NAME +' 님이 SRM을 통해 발송된 메일입니다. ' + '\n\n\n'; 

	mesgbody = mesgbody + '프로젝트번호: ' + ds_list.GetColumn(nRow, "PSPID") + '\n';
    mesgbody = mesgbody + '프로젝트명: ' + ds_list.GetColumn(nRow, "ZSITE_NM") + '\n\n';
    mesgbody = mesgbody + '준공승인이 나지 않아 완성검사를 신청하지 못하고 있습니다.' + '\n';
    mesgbody = mesgbody + '당 현장 수금 확인 바랍니다.' + '\n\n';
	mesgbody = mesgbody + '감사합니다.' + '\n' ;


	// 메일 형태 만들기
	ds_mail.ClearData();
	ds_mail.addRow();

	ds_mail.SetColumn(0, "SEND_USER_ID", G_USER_ID);
	ds_mail.SetColumn(0, "RECV_USER_ID", Substr(ds_list.GetColumn(nRow, "ZMAN"),1,7));
	ds_mail.SetColumn(0, "SEND_USER", send_user);
	ds_mail.SetColumn(0, "SEND_USER_NM", G_USER_NAME);

	ds_mail.SetColumn(0, "RECV_USER", recv_user);
	ds_mail.SetColumn(0, "RECV_USER_NM", recv_user_nm);

	ds_mail.SetColumn(0, "CC_USER", cc_user);
	ds_mail.SetColumn(0, "CC_USER_NM", cc_user_nm);

	ds_mail.SetColumn(0, "MESGTITL", mesgtitl);
	ds_mail.SetColumn(0, "MESGBODY", mesgbody);
	ds_mail.SetColumn(0, "PSPID", ds_list.GetColumn(nRow, "PSPID"));
	ds_mail.SetColumn(0, "POSID", ds_list.GetColumn(nRow, "POSID"));



	gfn_openPopup("PS0710001M",true);
}
