	gfn_alert("실측치입력 신규개발로 '실측치일반(신규)' 항목의 프로그램을 이용하시기 바랍니다."
	          + "\n기타 문의사항은 강북설치팀 우동훈 대리에게 문의바랍니다.");
	fn_close();
	return;
	//==========================================================================//	


	fn_clear();


for ( var b = 0; b < Length(this.Components); b++ ) {
  comp = this.components(b);
  comp.id.enable = false;
}



	// 조회 화면
	//ed_pjt.Text = PRE_PJT;
	//ed_hno.Text = PRE_HNO;

	// grid 1, 2 setting
	ufn_setGrid();
}
