function fn_cancel() {
	// dataset initailize
	ds_master.reset();
	ds_master1.DeleteAll();	

	ds_list.reset();
	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	// gfn_initDs(ds_cond, true);

 	// init data set
	ds_cond.SetColumn(0, "REP", "R");
	ds_master.SetColumn(0, "CESDLBJD", gfn_getCurrDate());
	ds_master.SetColumn(0, "CESDLHDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "CESDLSNM", G_USER_NAME);
	ufn_setGrid();
}
function fn_clear() {
	// dataset initailize
	ds_master.DeleteAll();
	ds_master1.DeleteAll();	

	ds_list.DeleteAll();
	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_list, true);

	// field initailize
	ed_pjt.Text = "";
	ed_hno.Text = "";
 	ed_hnos.Text = "";

 	// init data set  G_USER_NAME
	ds_master.SetColumn(0, "CESDLBJD", gfn_getCurrDate());
	ds_master.SetColumn(0, "CESDLHDM", gfn_getCurrDate());
	ds_master.SetColumn(0, "CESDLSNM", G_USER_NAME);

	ds_cond.SetColumn(0, "REP", "R");
}
