// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {
	gfn_initForm();	
	fn_clear();

	if(length(G_USER_GROUP_M) > 0) {
		for ( var b = 0; b < Length(this.Components); b++ ) 
		{
			comp = this.components(b);
			if(comp.getType() == "Div")
			{
				for ( var b = 0; b < Length(comp.Components); b++ ) 
				{
					//trim(comp2.getType()) == 'Grid' || 
					comp2 = comp.components(b);
					if(trim(comp2.getType()) == 'Edit' || trim(comp2.getType()) == 'Calendar' || 
					   trim(comp2.getType()) == 'TextArea') {
						comp2.enable = false;
					}
				}
			}
		}
		div_pop.ed_pjt.Enable = true;
		//div_pop.ed_hno.Enable = true;
	}	


	//====협력사만 출력 가능하게(우동훈대리/홍성달씨 요청)====
	user_grp = gfn_getUserGroup();
	if(user_grp == '10' || G_USER_ID == '2018900')
	{
		div_btn.btn_print.enable = true;
	}
	else
	{
		div_btn.btn_print.enable = false;
	}
	//=====================================================	

	// 조회 화면
	div_pop.ed_pjt.Text = PRE_PJT;
	div_pop.ed_hno.Text = PRE_HNO;

	// grid 1, 2 setting
	ufn_setGrid();
	fn_query();
}
