function fn_cancel() {
	// dataset initailize
	ds_master.reset();
	ds_master1.DeleteAll();	

	ds_list.reset();
	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	// gfn_initDs(ds_cond, true);

 	// init data set
	ds_cond.SetColumn(0, "REP", "C");
	for(var i=0; i<ds_combo1.GetRowCount(); i++){
		ds_combo1.SetColumn(i,"CHECK",0);
		ds_combo2.SetColumn(i,"CHECK",0);
	}
	ds_combo1.SetColumn(toNumber(ds_master.GetColumn(0,"CESDHDSZ"))-1,"CHECK",1);
	ds_combo2.SetColumn(toNumber(ds_master.GetColumn(0,"CESDHHSZ"))-1,"CHECK",1);
	ufn_setGrid();
}
function fn_clear() {
	// dataset initailize
	ds_master.DeleteAll();
	ds_master1.DeleteAll();	

	ds_list.DeleteAll();
	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_list, true);

	// field initailize
	div_pop.ed_pjt.Text = "";
	div_pop.ed_hno.Text = "";
 	div_pop.ed_hnos.Text = "";

 	// init data set
	ds_master.SetColumn(0, "CESDHBJD", gfn_getCurrDate());
	ds_cond.SetColumn(0, "REP", "C");
}
