function fn_afterQuery(errCode, errMsg) {
	// 메시지 처리 
	gfn_showSearchMsg(div_pop.Edit3);
	ds_combo1.SetColumn(toNumber(ds_master.GetColumn(0,"CESDHDSZ"))-1,"CHECK",1);
	ds_combo2.SetColumn(toNumber(ds_master.GetColumn(0,"CESDHHSZ"))-1,"CHECK",1);

	ufn_setGrid();
}
/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_afterSave(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showAlert("CE00001");
	} else {
		gfn_showAlert("CI00003");		
		SELFLG = "Y";
	}
}
/********************************************************************************
* 기      능   : 삭제 Callback  
********************************************************************************/
function fn_afterDelete(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");	
		SELFLG = "Y";
		fn_query();
	}

}
/********************************************************************************
* 기      능   : 출력전 저장 Callback  
********************************************************************************/
function fn_afterPrint(errCode, errMsg) {
	if ( ds_error.rowcount() > 0 ) {
		gfn_showMsg("CE00001");
	} else {
		fn_print2();
		SELFLG = "Y";
	}	
}

