/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅 
********************************************************************************/
function ufn_setGrid(){
	div_pop.grd_list.Redraw = false;
	ds_list.fireEvent = false;
	var rows;
	var cBKN;
	while(100 > ds_list.GetRowCount()){
		rows = ds_list.InsertRow( ds_list.GetRowCount() );
		ds_list.SetColumn(rows, "CESDIBSN", rows+1);	
	}
	ds_list.row = 0;
	ds_list.fireEvent = true;	
	div_pop.grd_list.Redraw = true;
}

function ufn_makeListDs(){
	var objFLR;
	var crow;
	ds_list.DeleteAll();

	for(var i=0; i<35; i++){
		objFLR = object("ed_FLR"+i);
		if(length(objFLR.text)>0){
			crow = ds_list.AddRow();
			ds_list.SetColumn(crow, "CESDCFLR", objFLR.text);
			ds_list.SetColumn(crow, "CESDCFMM", object("ed_FMM"+i).text);			
		}		
	}

}
