function ds_combo1_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="CHECK"){	
		for(var i=0; i<ds_combo1.GetRowCount(); i++){
			if(i!=nRow){
				if(varNewValue==1){
					ds_combo1.SetColumn(i, strColumnID, 0);
				} 
			}
		}
	}
}
function ds_combo2_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if(strColumnID=="CHECK"){	
		for(var i=0; i<ds_combo2.GetRowCount(); i++){
			if(i!=nRow){
				if(varNewValue==1){
					ds_combo2.SetColumn(i, strColumnID, 0);
				} 
			}
		}
	}
}

