function fn_print2() 
{
	ds_master_p.DeleteAll();
	ds_list_p.DeleteAll();
	ds_master_p.AddRow();

	// 저장할 데이터셋을 복사
	ds_master_p.SetColumn(0,"ADDR",ds_master.GetColumn(0,"ADDR"));
	ds_master_p.SetColumn(0,"CESDAREP",ds_master.GetColumn(0,"CESDAREP"));
	ds_master_p.SetColumn(0,"CESDHBJD",ds_master.GetColumn(0,"CESDHBJD"));
	ds_master_p.SetColumn(0,"CESDHDSZ",ds_master.GetColumn(0,"CESDHDSZ"));
	ds_master_p.SetColumn(0,"CESDHHDM",ds_master.GetColumn(0,"CESDHHDM"));
	ds_master_p.SetColumn(0,"CESDHHNO",ds_master.GetColumn(0,"CESDHHNO"));
	ds_master_p.SetColumn(0,"CESDHHSZ",ds_master.GetColumn(0,"CESDHHSZ"));
	ds_master_p.SetColumn(0,"CESDHNAP",ds_master.GetColumn(0,"CESDHNAP"));
	ds_master_p.SetColumn(0,"CESDHPJT",ds_master.GetColumn(0,"CESDHPJT"));
	ds_master_p.SetColumn(0,"CESDHREQ",ds_master.GetColumn(0,"CESDHREQ"));
	ds_master_p.SetColumn(0,"CESDHSNM",ds_master.GetColumn(0,"CESDHSNM"));
	ds_master_p.SetColumn(0,"CESDHUPD",ds_master.GetColumn(0,"CESDHUPD"));
	ds_master_p.SetColumn(0,"NAME1",ds_master.GetColumn(0,"NAME1"));
	ds_master_p.SetColumn(0,"NAMET",ds_master.GetColumn(0,"NAMET"));
	ds_master_p.SetColumn(0,"ZSITE_NM",ds_master.GetColumn(0,"ZSITE_NM"));
	ds_master_p.SetColumn(0,"ZSPEC1",ds_master.GetColumn(0,"ZSPEC1"));
	ds_master_p.SetColumn(0,"ZSPEC11",ds_master.GetColumn(0,"ZSPEC11"));
	ds_master_p.SetColumn(0,"ZSPEC7",ds_master.GetColumn(0,"ZSPEC7"));

	if(ds_combo1.GetColumn(0,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK1","V");
	} else if(ds_combo1.GetColumn(1,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK2","V");
	} else if(ds_combo1.GetColumn(2,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK3","V");
	} else if(ds_combo1.GetColumn(3,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK4","V");
	} 
	if(ds_combo2.GetColumn(0,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK5","V");
	} else if(ds_combo2.GetColumn(1,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK6","V");
	} else if(ds_combo2.GetColumn(2,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK7","V");
	} else if(ds_combo2.GetColumn(3,"CHECK") == '1') {
		ds_master_p.SetColumn(0,"CHK8","V");
	}

	ds_master_p.SetColumn(0,"CESDHHDM",gfn_formatDate(ds_master_p.GetColumn(0,"CESDHHDM"),'.'));
	ds_master_p.SetColumn(0,"CESDHBJD",gfn_formatDate(ds_master_p.GetColumn(0,"CESDHBJD"),'.'));
	ds_master_p.SetColumn(0,"CESDHNAP",gfn_formatDate(ds_master_p.GetColumn(0,"CESDHNAP"),'.'));

	if (ds_master_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	ds_list.Filter("trim(CESDIBSZ)>''");
	ds_list_p.copyf(ds_list);
	ds_list.Filter("1=1");

	// UbiReport Call
	Dialog("PS11::PS1101007P.xml", "");
}
