	tit_clearActionInfo();		
	tit_addMultiActionInfo("ps11:PS1101009B_D2");		
	tit_addMultiActionInfo("ps11:PS1101009B_D1");
	tit_addSelectExActionInfo("ps11:PS1101009B_S3", "ps11:PS1101003B_D3", "");

	tit_callService(
		""
		, ""
		, "ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterDelete"
		, true);
}
