	// grid0.CreateFormat();
	// grid1.CreateFormat();
	// return;	
	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	tit_clearActionInfo();		
	tit_addMultiActionInfo("ps11:PS1101009A_I1");		
	tit_addMultiActionInfo("ps11:PS1101009A_I2");
	tit_addSelectExActionInfo("ps11:PS1101003A_S2", "ps11:PS1101003A_I3", "");

	// 조회 화면
	// tit_addMultiActionInfo("ps11:PS1101009B_U1");		
	// tit_addMultiActionInfo("ps11:PS1101009B_D1");
	// tit_addMultiActionInfo("ps11:PS1101009A_I2");

	tit_callService(
		""
		, ""
		, "ds_master1=ds_master1 ds_list2=ds_list2 ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterSave"
		, true);	
}	
