/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅 
********************************************************************************/
function ufn_setGrid(){
	grd_list.Redraw = false;
	ds_list.fireEvent = false;
	var rows;
	while(100 > ds_list.GetRowCount()){
		rows = ds_list.InsertRow( ds_list.GetRowCount() );
		//ds_list.SetColumn(rows, "CESDKSEQ", rows+1);	
	}
	ds_list.row = 0;
	ds_list.fireEvent = true;	
	grd_list.Redraw = true;
}

function ufn_verifyField(gbn){
	// 해더 부분
	if(gfn_isNull(ed_pjt, true, Static0.Text)) return false; // 프로젝트번호
	if(gfn_isNull(Edit9, true, Static5.Text)) return false;	// 실측자
	if(gfn_isNull(Calendar2, true, Static3.Text)) return false; // 현장요구일자
	if(gfn_isNull(Edit11, true, Static9.Text)) return false; // 유효폭


	// 리스트 부분
	var curFlr;
	var seaRow=0;

	ds_list1.DeleteAll();
	ds_list2.DeleteAll();
	// ds_list.Filter("CESDKFFL>'' || CESDKTFL>'' || CESDKFHX>'' || CESDKDWN>'' || CESDKBGO>''");
	ds_list.Filter("trim(CESDKFFL)>'' || trim(CESDKTFL)>'' || trim(CESDKFHX)>'' || trim(CESDKDWN)>'' || trim(CESDKBGO)>''");
	ds_list1.copyf(ds_list);
	ds_list.UnFilter();

	if(ds_list1.GetRowCount()==0){
		//Trace("CAR(좌)를 한개이상 입력해 주십시오.");
		gfn_showMsg("CW00136");
		return false;
	}
	var fflr;
	var tflr;
	for(var i=0; i<ds_list1.GetRowCount(); i++){	
		fflr = ds_list1.GetColumn(i, "CESDKFFL");
		tflr = ds_list1.GetColumn(i, "CESDKTFL");
		if(length(fflr)>0 && length(tflr)>0){
/*		
			if(toNumber(fflr) >= toNumber(tflr)){
				var arr = ["시작층", "종료층"];
				gfn_showMsg("CW00133", arr);
				return false;
			}
			for(var j=0; j<ds_list1.GetRowCount(); j++){
				if(j < i){	
					// trace(toNumber(ds_list1.GetColumn(j, "CESDKFFL"))+"||"+toNumber(ds_list1.GetColumn(j, "CESDKTFL"))+"||"+toNumber(fflr));		
					if(toNumber(ds_list1.GetColumn(j, "CESDKFFL")) >= toNumber(fflr) 
						|| toNumber(ds_list1.GetColumn(j, "CESDKTFL")) > toNumber(fflr)){

						gfn_showMsg("CW00138", i+"");
						return false;
					}					
				}
			}
*/			
		} else {
			gfn_showMsg("CW00001", i+"행의 시작층/종료층");
			return;
		}
	}	

	return true;	
}
function ufn_setSaveDs(){
	var mrow = 0;	
	ds_master1.DeleteAll();

	if(length(ed_hnos.Text)>0 && indexOf(ed_hnos.text, ",")!=-1){
		var hnos = Split(ed_hnos.text, ",");

		for(var i=0; i<length(hnos); i++){
			if(length(hnos[i])==3){
				mrow = ds_master1.AddRow();
				ds_master1.CopyRow(mrow, ds_master, 0);			
				ds_master1.SetColumn(mrow, "CESDJHNO", hnos[i]);
				ds_master1.SetColumn(mrow, "CESDJPJT", ed_pjt.Text);

				ufn_setListDs(ed_pjt.Text, hnos[i]);
			}					
		}		

	} else { 
		if(gfn_isNull(ed_hno, true, Static2.Text)) return false;

		mrow = ds_master1.AddRow();
		ds_master1.CopyRow(mrow, ds_master, 0);	
		ds_master1.SetColumn(mrow, "CESDJPJT", ed_pjt.Text);
		ds_master1.SetColumn(mrow, "CESDJHNO", ed_hno.Text);

		ufn_setListDs(ed_pjt.Text, ed_hno.Text);
	}

	return true;
}
function ufn_setListDs(pjt, hno){
	var crow=0;
	var cseq;
	for(var j=0; j<ds_list1.GetRowCount(); j++){
		crow = ds_list2.AddRow();
		ds_list2.CopyRow(crow, ds_list1, j);
		ds_list2.SetColumn(crow, "CESDKPJT", pjt);
		ds_list2.SetColumn(crow, "CESDKHNO", hno);

		cseq = lpad(j+1, "0", 3);
		ds_list2.SetColumn(crow, "CESDKSEQ", cseq);		
	}
}


