function btn_popProj_OnClick(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
		arg += " rep=F";
	// 조회 화면
	//gfn_openPopup("PS9602001B", true, arg);	
	gfn_openPopup("PS9602001A", true, arg);

	if(length(ed_hnos.Text)>0){
		var hnos = split(ed_hnos.Text, ",");
		ed_hno.Text = hnos[0];
	}

	fn_query();
}

