function fn_query() {	
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static0.Text)) return;
	if(gfn_isNull(div_pop.ed_hno, true, div_pop.Static2.Text)) return;
	ds_list.DeleteAll();

	tit_clearActionInfo();
	//tit_addSearchActionInfo("ps11:PS1101009A_S1");

	// 조회 화면
	tit_addSearchActionInfo("ps11:PS1101009B_S1");
	tit_addSearchActionInfo("ps11:PS1101009B_S2");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
