function fn_print2() 
{
	ds_master_p.DeleteAll();
	ds_list_p.DeleteAll();
	ds_master_p.AddRow();

	// 저장할 데이터셋을 복사
	ds_master_p.SetColumn(0,"ADDR",ds_master.GetColumn(0,"ADDR"));
	ds_master_p.SetColumn(0,"CESDAREP",ds_master.GetColumn(0,"CESDAREP"));
	ds_master_p.SetColumn(0,"CESDJBJD",ds_master.GetColumn(0,"CESDJBJD"));
	ds_master_p.SetColumn(0,"CESDJHDM",ds_master.GetColumn(0,"CESDJHDM"));
	ds_master_p.SetColumn(0,"CESDJHNO",ds_master.GetColumn(0,"CESDJHNO"));
	ds_master_p.SetColumn(0,"CESDJJJX",ds_master.GetColumn(0,"CESDJJJX"));
	ds_master_p.SetColumn(0,"CESDJNAP",ds_master.GetColumn(0,"CESDJNAP"));
	ds_master_p.SetColumn(0,"CESDJPJT",ds_master.GetColumn(0,"CESDJPJT"));
	ds_master_p.SetColumn(0,"CESDJREQ",ds_master.GetColumn(0,"CESDJREQ"));
	ds_master_p.SetColumn(0,"CESDJSAY",ds_master.GetColumn(0,"CESDJSAY"));
	ds_master_p.SetColumn(0,"CESDJSNM",ds_master.GetColumn(0,"CESDJSNM"));
	ds_master_p.SetColumn(0,"CESDJUPD",ds_master.GetColumn(0,"CESDJUPD"));
	ds_master_p.SetColumn(0,"NAME1",ds_master.GetColumn(0,"NAME1"));
	ds_master_p.SetColumn(0,"NAMET",ds_master.GetColumn(0,"NAMET"));
	ds_master_p.SetColumn(0,"ZSITE_NM",ds_master.GetColumn(0,"ZSITE_NM"));
	ds_master_p.SetColumn(0,"ZSPEC1",ds_master.GetColumn(0,"ZSPEC1"));
	ds_master_p.SetColumn(0,"ZSPEC11",ds_master.GetColumn(0,"ZSPEC11"));
	ds_master_p.SetColumn(0,"ZSPEC7",ds_master.GetColumn(0,"ZSPEC7"));

	if(ds_master_p.GetColumn(0,"CESDJSAY") == '1') {
		ds_master_p.SetColumn(0,"CHK1","V");
	} else if(ds_master_p.GetColumn(0,"CESDJSAY") == '2') {
		ds_master_p.SetColumn(0,"CHK2","V");
	} else if(ds_master_p.GetColumn(0,"CESDJSAY") == '3') {
		ds_master_p.SetColumn(0,"CHK3","V");
	} 

	ds_master_p.SetColumn(0,"CESDJHDM",gfn_formatDate(ds_master_p.GetColumn(0,"CESDJHDM"),'.'));
	ds_master_p.SetColumn(0,"CESDJBJD",gfn_formatDate(ds_master_p.GetColumn(0,"CESDJBJD"),'.'));
	ds_master_p.SetColumn(0,"CESDJNAP",gfn_formatDate(ds_master_p.GetColumn(0,"CESDJNAP"),'.'));

	if (ds_master_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}

	ds_list.Filter("trim(CESDKFFL)>'' || trim(CESDKTFL)>'' || trim(CESDKFHX)>'' || trim(CESDKDWN)>'' || trim(CESDKBGO)>''");
	ds_list_p.copyf(ds_list);
	ds_list.Filter("1=1");

	// UbiReport Call
	Dialog("PS11::PS1101009P.xml", "");
}
