		, true);
}
/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save() {
	if(!ufn_verifyField()) return;
	if(!ufn_setSaveDs()) return;
	//해당데이타를 저장하시겠습니까?
	if(!gfn_showConfirm( "CI00014", "해당 데이터" )) return;

	// 조회 화면
	tit_addMultiActionInfo("ps11:PS1102001B_U1");		
	tit_addMultiActionInfo("ps11:PS1102001B_D1");
	tit_addMultiActionInfo("ps11:PS1102001A_I2");	

	tit_callService(
		""
		, ""
		, "ds_master1=ds_master1 ds_list2=ds_list2 ds_cond=ds_cond"
		, ""
		, ""
		, "fn_afterSave"
		, true);	
}	
