function fn_cancel() {
	// dataset initailize
	ds_master.reset();
	ds_master1.DeleteAll();	

	ds_list.reset();
	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	// gfn_initDs(ds_cond, true);

 	// init data set
	ds_cond.SetColumn(0, "REP", "S");
	ufn_setGrid();
}
function fn_clear() {
	// dataset initailize
	ds_master.DeleteAll();
	ds_master1.DeleteAll();	

	ds_list.DeleteAll();
	ds_list1.DeleteAll();
	ds_list2.DeleteAll();

	gfn_initDs(ds_cond, true);
	gfn_initDs(ds_master, true);
	gfn_initDs(ds_list, true);

	// field initailize
	div_pop.ed_pjt.Text = "";
	div_pop.ed_hno.Text = "";
 	div_pop.ed_hnos.Text = "";

 	// init data set
	//ds_master.SetColumn(0, "CESDFBJD", gfn_getCurrDate());
	ds_cond.SetColumn(0, "REP", "S");
}
