/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅 
********************************************************************************/
function ufn_setGrid(){
	div_pop.grd_list.Redraw = false;
	ds_list.fireEvent = false;
	var rows;
	while(100 > ds_list.GetRowCount()){
		rows = ds_list.InsertRow( ds_list.GetRowCount() );

	}
	ds_list.row = 0;
	ds_list.fireEvent = true;	
	div_pop.grd_list.Redraw = true;
}
function ufn_setSaveDs(){
	var mrow = 0;
	ds_master1.DeleteAll();

	if(length(div_pop.ed_hnos.Text)>0 && indexOf(div_pop.ed_hnos.text, ",")!=-1){
		var hnos = Split(div_pop.ed_hnos.text, ",");

		for(var i=0; i<length(hnos); i++){
			if(length(hnos[i])==3){
				mrow = ds_master1.AddRow();
				ds_master1.CopyRow(mrow, ds_master, 0);			
				ds_master1.SetColumn(mrow, "CESDFHNO", hnos[i]);
				ds_master1.SetColumn(mrow, "CESDFPJT", div_pop.ed_pjt.Text);

				ufn_setListDs(div_pop.ed_pjt.Text, hnos[i]);
			}					
		}		

	} else { 
		if(gfn_isNull(div_pop.ed_hno, true, div_pop.Static2.Text)) return false;

		mrow = ds_master1.AddRow();
		ds_master1.CopyRow(mrow, ds_master, 0);	
		ds_master1.SetColumn(mrow, "CESDFPJT", div_pop.ed_pjt.Text);
		ds_master1.SetColumn(mrow, "CESDFHNO", div_pop.ed_hno.Text);

		ufn_setListDs(div_pop.ed_pjt.Text, div_pop.ed_hno.Text);
	}

	return true;
}
function ufn_setListDs(pjt, hno){
	var crow=0;
	for(var j=0; j<ds_list1.GetRowCount(); j++){
		crow = ds_list2.AddRow();
		ds_list2.CopyRow(crow, ds_list1, j);
		ds_list2.SetColumn(crow, "CESDGPJT", pjt);
		ds_list2.SetColumn(crow, "CESDGHNO", hno);
		ds_list2.SetColumn(crow, "CESDGSEQ", j+1);		
	}
}

function ufn_verifyField(gbn){
	// 해더 부분
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.Static0.Text)) return false; // 프로젝트번호
	if(gfn_isNull(div_pop.Edit9, true, div_pop.Static5.Text)) return false;	// 실측자
	if(gfn_isNull(div_pop.Calendar2, true, div_pop.Static3.Text)) return false; // 현장요구일자
	if(gfn_isNull(div_pop.Edit11, true, div_pop.Static9.Text)) return false; // 출구유효폭(JJ)
	if(gfn_isNull(div_pop.Edit12, true, div_pop.Static10.Text)) return false; // 출구유효폭(H)	

	// 리스트 부분
	var curFlr;
	var seaRow;

	ds_list1.DeleteAll();
	ds_list2.DeleteAll();
	ds_list.Filter("trim(CESDGFLR)>'' || trim(CESDGJDX)>'' || trim(CESDGNAT)>'' || trim(CESDGBGO)>''");
	ds_list1.copyf(ds_list);
	ds_list.UnFilter();

	if(ds_list1.GetRowCount()==0){
		//Trace("한개이상 입력해 주십시오.");
		gfn_showMsg("CW00136");
		return false;
	}
/*	
	for(var i=0; i<ds_list1.GetRowCount(); i++){		
		if(length(ds_list1.GetColumn(i, "CESDGFLR"))>0){
			curFlr = ds_list1.GetColumn(i, "CESDGFLR");
			seaRow = ds_list1.SearchRow("CESDGFLR='"+curFlr+"'", 0, i);

			if(seaRow!=-1){
				// Trace("동일한 층이 있습니다.");
				gfn_showMsg("CW00138", i+"");
				return false;
			}
		} else {
			gfn_showMsg("CW00001", i+"행의 층");
			return;
		}
	}		
*/
	return true;	
}
