		, "ds_master=ds_master"
		, ""
		, ""
		, "fn_afterPrint"
		, true);	
}
function fn_print2() 
{
	ds_list_p.DeleteAll();
	ds_master_p.DeleteAll();
	ds_master_p.AddRow();

	// 저장할 데이터셋을 복사
	ds_master_p.CopyRow(0,"ds_master",0);
	if (ds_master_p.rowcount < 1)
	{
		gfn_showAlert("CW00013");
		return;
	}
	ds_master_p.SetColumn(0,"CESDFHDM",gfn_formatDate(ds_master_p.GetColumn(0,"CESDFHDM"),'.'));
	ds_master_p.SetColumn(0,"CESDFBJD",gfn_formatDate(ds_master_p.GetColumn(0,"CESDFBJD"),'.'));
	ds_master_p.SetColumn(0,"CESDFNAP",gfn_formatDate(ds_master_p.GetColumn(0,"CESDFNAP"),'.'));

	ds_list.Filter("trim(CESDGFLR)>'' || trim(CESDGJDX)>'' || trim(CESDGNAT)>'' || trim(CESDGBGO)>''");
	ds_list_p.copyf(ds_list);
	ds_list.Filter("1=1");

	for(var i = 0; i < ds_list_p.rowcount; i++)
	{
		ds_list_p.SetColumn(i,"CESDGSEQ",i+1);
	}

	// UbiReport Call
	Dialog("PS11::PS1102001P.xml", "");
}
