function fn_query() {
	if(gfn_isNull(ed_pjt, true, st_pjt.value)) return;
	if(gfn_isNull(ed_hno, true, st_hno.Value)) return;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1102003A_S1");
	tit_addSearchActionInfo("ps11:PS1102003A_S2");
	tit_addSearchActionInfo("ps11:PS1102003A_S6");

	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_list=ds_list ds_sndsys=ds_sndsys"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
