/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save() {
	if(gfn_isNull(ed_snm, true, st_snm.Value)) return false; // 실측자
	if(gfn_isNull(cal_hdm, true, st_hdm.Value)) return false;	// 현장요구일
	if(gfn_isNull(ed_jjx, true, st_jjx.Value)) return false; // 출구유효폭
	if(ds_master.GetColumn(0,"CESDDGUB") > '')
	{
		if(gfn_isNull(cal_dat, true, st_dat.Value)) return false; // 재측정예정일
	}

	//ds_list.Filter("TRIM(CESDEFLR) > '' || TRIM(CESDEJHX) > '' || TRIM(CESDEJDX) > '' || TRIM(CESDEMHX) > '' || TRIM(CESDENAT) > '' || TRIM(CESDEBGO) > '' ");
	ds_list.Filter("length(TRIM(CESDEFLR)) > 0 || length(TRIM(CESDEJHX)) > 0 || length(TRIM(CESDEPLX)) > 0 || length(TRIM(CESDEMHX)) > 0 || length(TRIM(CESDENAT)) > 0 || length(TRIM(CESDEBGO)) > 0 ");
	ds_list2.copyf(ds_list);
	ds_list.Filter("1=1");

	if(ds_list2.rowcount == 0)
	{
		gfn_showMsg("CW00136");
		return;
	}
	else
	{
		for( var i=0; i<ds_list2.rowcount; i++)
		{
			if(length(ds_list2.GetColumn(i,"CESDEFLR")) == 0)
			{
				gfn_showMsg("CW00001",st_floor.value);
				grd_list.SetCellPos(1);
				return;
			}
		}
	}	

	for(var i=0; i<ds_list2.GetRowCount(); i++){
		if( length(ds_list2.GetColumn(i, "CESDEFLR")) > 0 ){
			var curFlr = ds_list2.GetColumn(i, "CESDEFLR");
			var seaRow = ds_list2.SearchRow("CESDEFLR='"+curFlr+"'", 0, i-1);

			if(seaRow != -1){
				gfn_showMsg("CW00134");
				return false;
			}
		} 
	}
	if(!ufn_setSaveDs()) return;


    //=====================전기기종이 WBSS2 일경우=====================
	for( var i=0; i<ds_master1.rowcount; i++)
	{
		ds_cond2.SetColumn(0,"PSPID", ds_master1.GetColumn(i, "CESDDPJT")   );
		ds_cond2.SetColumn(0,"POSID", ds_master1.GetColumn(i, "CESDDPJT") + ds_master1.GetColumn(i, "CESDDHNO")  );
		tit_clearActionInfo();	
		tit_addSearchActionInfo("ps11:PS1102003A_S4");  	
		tit_callService(
			  ""
			, ""
			, "ds_cond2=ds_cond2"
			, "ds_check=ds_check"  
			, ""
			, ""  //"fn_afterQuery"  
			, ""
			, ""
			, true);

		/*
		if(ds_check.GetColumn(i,"ZSPEC12") == "WBSS2")
		{
			for( var j=0; j<ds_list1.rowcount; j++)
			{
				if( ds_master1.GetColumn(i, "CESDDPJT") == ds_list1.GetColumn(j, "CESDEPJT") &&
				    ds_master1.GetColumn(i, "CESDDHNO") == ds_list1.GetColumn(j, "CESDEHNO") &&
				    parseInt(ds_list1.GetColumn(j, "CESDEPLX")) < 235 )
				{				
					//--gfn_showMsg("CW00022",'전기기종이 WBSS2일경우 CP 배치 불가 및 JD값은 235이상만 가능하니 ' );
					//gfn_showAlert("CW00022",'전기기종이 WBSS2일경우 CP 배치 불가 및 JD값은 235이상만 가능하니 ' );
					//--grd_list.SetCellPos(3);
					//return;
					//해당데이타를 진행여부 체크
					if(!gfn_showConfirm( "CI00024", "주의(JD값 235미만)! CP JAMB일 경우 CP배치 불가하므로 JD값을 235이상으로" +
					                                " 입력할 것, 품목표 참조. " )) return;
				}
			}
		}
		*/
