/********************************************************************************
* 기      능   : master / detail 데이터셋 초기 데이터 셋팅
********************************************************************************/
function ufn_setGrid(){
	grd_list.Redraw = false;
	ds_list.fireEvent = false;
	var rows;
	while(80 > ds_list.GetRowCount()){
		rows = ds_list.InsertRow( ds_list.GetRowCount() );

		ds_list.SetColumn(rows,"CESDEPJT",ds_master.GetColumn(0,"CESDDPJT"));
		ds_list.SetColumn(rows,"CESDEHNO",ds_master.GetColumn(0,"CESDDHNO"));
	}
	ds_list.row = 0;
	ds_list.fireEvent = true;
	grd_list.Redraw = true;
}

