/********************************************************************************
* 기      능   : 그리드의 체크박스 하나만 되도록
********************************************************************************/
function ds_sill_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "COL1":
			ds_sill.SetColumn(0,"COL2","0");
			ds_sill.SetColumn(0,"COL3","0");
			break;
		case "COL2":
			ds_sill.SetColumn(0,"COL1","0");
			ds_sill.SetColumn(0,"COL3","0");
			break;
		case "COL3":
			ds_sill.SetColumn(0,"COL1","0");
			ds_sill.SetColumn(0,"COL2","0");
			break;
	}
	obj.fireevent = true;
}
