/********************************************************************************
* 기      능   : 그리드 데이타셋값 변경시 비고란에 '품목표 참조' 세팅
********************************************************************************/
function ds_list_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	obj.fireevent = false;
	switch( strColumnID ) {
		case "CESDEFLR":
		case "CESDEJHX":
		case "CESDEPLX":
		case "CESDEMHX":
		case "CESDENAT":
			if(length(ds_list.GetColumn(nRow,"CESDEBGO")) == 0) {
				ds_list.SetColumn(nRow,"CESDEBGO","품목표 참조");
			}	
	}
	obj.fireevent = true;
}

