function fn_query() {
	if(gfn_isNull(div_pop.ed_pjt, true, div_pop.st_pjt.value)) return;
	if(gfn_isNull(div_pop.ed_hno, true, div_pop.st_hno.Value)) return;

	tit_clearActionInfo();
	tit_addSearchActionInfo("ps11:PS1102003B_S1");
	tit_addSearchActionInfo("ps11:PS1102003B_S2");


	tit_callService(
        ""
        , ""
        , "ds_cond=ds_cond"
        , "ds_master=ds_master ds_list=ds_list"
        , ""
        , "fn_afterQuery"
        , true
        , true);
}
